void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a=.6+.1*mod(.03*iTime,1.);
    float b=sqrt(1.-a*a);
    float c=a/b;
    mat2 m=mat2(a,-b,b,a);

    vec2 uv = fragCoord.xy / iResolution.xy;
	uv = vec2(-.6,-.2)+.7*uv;
	uv.x *= iResolution.x/iResolution.y;
    for(int i=8;i>0;i--){
     while(uv.y>0.||uv.x>c*uv.y)uv*=m;
     uv*=.85;
     uv+=vec2(.07,-.035);
    }
	float d = .4+.2*atan(uv.x,uv.y);
	fragColor = vec4(vec3(d,d*d,1.-d),1.);
    if(uv.x-uv.y<.05)fragColor=vec4(0.,.5,0.,1.);
}
