// Fork of "floating colors: blue on green" by FabriceNeyret2. https://shadertoy.com/view/wdVXzc
// 2019-11-25 10:07:09

// variant of https://shadertoy.com/view/tdKXzc

void mainImage( out vec4 O, vec2 U )
{

// UIT-R BT 601 def of luma https://fr.wikipedia.org/wiki/Luminance#Matri%C3%A7age
// Y '= .299  R' + .587  G' + .114  B'

// choose G=g with same luminance than B=1:
    float g = .114/.587;     //  ( = .195 )

// choose R=1, G=gr with same luminance than G=1:  .299 +.587*gr = .587 
    float gr = 1.- .299/.587; // ( = .49 ) 
// choose B=1, G=gr with same luminance than G=1:  .144 +.587*br = .587 
    float gb = 1.- .114/.587; // ( = .805 ) 
//  float gb = iMouse.y/R.y;

    vec2 R = iResolution.xy;
    if ( U.x-.5 == floor(R.x/2.) || U.y-.5 == floor(R.y/2.) ) { O-=O; return; } // black lines
    U = ( 2.*U - R ) / R.y;

    float m = smoothstep(0.,6./R.y,length(U)-.5);


    O = mix( vec4(1,U.x > 0. ? 0. : gr,0,1), vec4(0,U.y<0. ? 0. : gb,1,1), m );


    O = pow( O, vec4(1./2.2) ); // gamma correction
}
