float hash21(vec2 p) {
    return fract(abs(cos(24.54*fract(dot(p,p+vec2(23.23,54.553))))));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy )/ iResolution.y;

    uv*=7.;
    vec2 uuv = uv;

    vec2 gid = floor(uv+ vec2(iTime,0.));


    uv=mod(uv+vec2(iTime*abs(1.+abs(gid.y)/2.),0.),1.)-.5;

    uv*=4.*length(uuv+2.*vec2(tan(cos(iTime)),tan(sin(iTime))));
    float d = 1.-(length(uv)-.5+uv.x/2.);
    vec3 col = vec3(0);

     col.r =d*hash21(gid);
     col.g =d*hash21(gid.yx);
     col.b =d*hash21(vec2(hash21(gid.yx),hash21(gid)));
    fragColor = vec4(col,1.0);
}
