void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragCoord += iTime;
    mat3 samples = mat3(0.0);
    vec2 uv = vec2(0.0);

    for (int i=-1; i < 2; i++) {
        for (int j=-1; j < 2; j++) {
            uv = (fragCoord + vec2(i, j)) / iResolution.xy;
            samples[i + 1][j + 1] = texture(iChannel0, uv).x;
        }
    }

    bool onBound = false;
    for (int i=0; i < 3; i++) {
        for (int j=0; j < 3; j++) {
            if (i != 1 && j != 1) {
                float gradient1 = samples[1][1] - samples[i][j];
                float gradient2 = samples[2 - i][2 - j] - samples[1][1];
                if ((gradient1 >= 0.015 && gradient2 >= 0.015) || (gradient1 < -0.015 && gradient2 < -0.015)) {
                    onBound = true;
                    break;
                }
            }
        }
    }

    vec3 color = onBound ? vec3(0.0) : vec3(1.0);
    fragColor = vec4(color, 1.0);
}
