// --- 397 chars

#define S(v) smoothstep(6./R.y,0.,v)

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y, 
          V = U * mat2(3,1,-1,3) / 3.2,
          X = vec2(0,1);
    float x = abs(U.x), y = U.y-.5;
    O = vec4(1);
    if ( abs(U.y+.2) < .3)                                     // tail
        O -= S(  abs( 2.*V.x -.2*sin(12.*V.y+1.) +.74 ) +.12*U.y-.02 );

    if (U.y<.75) O -= S( length(U/vec2(1,2)-.42*X) - .2 )      // head
                     -S( length(U*vec2(1,2)-1.5*X) - .2 );     // ears
    if (abs(U.y)<.5) O -= S(x-y*y*(3.+y+y)/5.);                // body
    if (U.y<-.5) O -= S(.3*x-U.y-.557);                        // arms
}

