// "Twisted Curves 2" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

/*
 Original (https://www.shadertoy.com/view/lsVXW1) updated for higher-order curves
 and surface bump.

 Note: no loops are required to define the curves.
*/

/*
 Includes knots, linked rings, Moebius strips

 The three sliders (left->right) control the order, twist and connectivity;
 play with them to determine what does what (keep the middle value low to avoid artifacts).

 See: www.fractalforums.com/new-theories-and-research/not-fractal-but-funny-trefoil-knot-routine/
*/

float SmoothBump (float lo, float hi, float w, float x);
float ShowInt (vec2 q, vec2 cBox, float mxChar, float val);
float Fbm2 (vec2 p);
vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
vec4 Loadv4 (int idVar);

mat3 vuMat;
vec3 ltDir;
float dstFar, tCur, cvOrd, cvWrapI, cvWrapF, cvWid;
const float wScl = 40.;
const float pi = 3.14159;

#define BUMP 1

float ObjDf (vec3 p)
{
  vec2 q;
  float twAng, rAng, s, r;
#if BUMP
  q = Rot2D (p.xz, 0.3 * tCur);
  r = 1. + 0.5 * SmoothBump (0.46, 0.54, 0.02, atan (q.y, q.x) / (2. * pi) + 0.5);
#else
  r = 1.;
#endif
  twAng = (cvWrapI + cvWrapF / cvOrd) * atan (p.z, p.x);
  q = Rot2D (vec2 (length (p.xz) - 3., p.y), twAng);
  s = 2. * pi / cvOrd;
  rAng = s * (floor ((0.5 * pi - atan (q.x, q.y)) / s + 0.5));
  q = Rot2D (q, - rAng);
  return 0.4 * (length (max (abs (vec2 (q.x - r, q.y)) - vec2 (cvWid), 0.)) - 0.5 * cvWid);
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 220; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = 0; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 BgCol (vec3 rd)
{
  vec2 u;
  float a;
  rd = rd * vuMat;
  a = 0.5 * atan (length (rd.xy), rd.z);
  rd = normalize (vec3 (rd.xy * tan (a), 1.));
  u = vec2 (0.1 * tCur + rd.xy / rd.z);
  return mix (mix (vec3 (0., 0., 0.6), vec3 (1.), 1.4 * Fbm2 (u)),
     vec3 (0.3, 0.3, 0.6), smoothstep (0.35 * pi, 0.4 * pi, a));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 vn, col;
  float dstObj;
  cvWid = mix (0.15, 0.05, smoothstep (10., 30., cvOrd));
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col = vec3 (0.3, 0.3, 0.6);
    col *= 0.2 + 0.8 * max (dot (vn, ltDir), 0.) +
       0.2 * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
    col = mix (col, BgCol (reflect (rd, vn)), 0.5);
  } else col = vec3 (0.6, 0.8, 0.9);
  return clamp (col, 0., 1.);
}

vec3 ShowWg (vec2 uv, vec2 canvas, vec3 col, float cvOrd, float cvWrapI, float cvWrapF )
{
  vec4 wgBx[3];
  vec3 cc;
  vec2 ut, ust;
  float vW[3], asp;
  asp = canvas.x / canvas.y;
  wgBx[0] = vec4 (0.35 * asp, 0., 0.012 * asp, 0.18);
  wgBx[1] = vec4 (0.4 * asp, 0., 0.012 * asp, 0.18);
  wgBx[2] = vec4 (0.45 * asp, 0., 0.012 * asp, 0.18);
  vW[0] = cvOrd / wScl;
  vW[1] = cvWrapI / wScl;
  vW[2] = cvWrapF / wScl;
  for (int k = 0; k < 3; k ++) {
    cc = vec3 (0.3, 0.3, 1.);
    ut = 0.5 * uv - wgBx[k].xy;
    ust = abs (ut) - wgBx[k].zw * vec2 (0.7, 1.);
    if (max (ust.x, ust.y) < 0.) {
      if  (min (abs (ust.x), abs (ust.y)) * canvas.y < 2.) col = vec3 (0.3);
      else col = (mod (0.5 * ((0.5 * uv.y - wgBx[k].y) / wgBx[k].w - 0.99), 0.1) *
         canvas.y < 6.) ? vec3 (1., 1., 0.) : vec3 (0.6);
    }
    ut.y -= (vW[k] - 0.5) * 2. * wgBx[k].w;
    ut = abs (ut) * vec2 (1., 1.5);
    if (max (abs (ut.x), abs (ut.y)) < 0.02 && max (ut.x, ut.y) > 0.008) col = cc;
    col = mix (col, cc, ShowInt (0.5 * uv - (wgBx[k].xy + wgBx[k].zw) * vec2 (1., -1.) -
       vec2 (0.0045, -0.06), 0.025 * vec2 (asp, 1.), 2.,
       clamp (floor (wScl * vW[k]), 0., wScl - 1.)));
  }
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat;
  vec3 ro, rd, col, cvParm;
  vec2 canvas, uv, uvs;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uvs = uv;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  cvParm = Loadv4 (0).xyz;
  cvOrd = wScl * cvParm.x;
  cvWrapI = wScl * cvParm.y;
  cvWrapF = wScl * cvParm.z;
  stDat = Loadv4 (1);
  el = stDat.x;
  az = stDat.y;
  dstFar = 50.;
  vuMat = StdVuMat (el, az);
  rd = vuMat * normalize (vec3 (uv, 7.));
  ro = vuMat * vec3 (0., 0., -30.);
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
  col = ShowScene (ro, rd);
  col = ShowWg (uv, canvas, col, cvOrd, cvWrapI, cvWrapF);
  uvs *= uvs * uvs;
  uvs *= uvs * uvs;
  col *= mix (0.8, 1., pow (1. - 0.5 * length (uvs * uvs), 4.));
  fragColor = vec4 (col, 1.);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float DigSeg (vec2 q)
{
  return (1. - smoothstep (0.13, 0.17, abs (q.x))) *
     (1. - smoothstep (0.5, 0.57, abs (q.y)));
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  float d;
  int k, kk;
  const vec2 vp = vec2 (0.5, 0.5), vm = vec2 (-0.5, 0.5), vo = vec2 (1., 0.);
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.8, 2.3);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);  DSG (q.xy - vp);  DSG (q.xy - vm);  DSG (q.yx);
  DSG (q.xy + vm);  DSG (q.xy + vp);  DSG (q.yx + vo);
  return d;
}

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, sgn, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (min (q.x, q.y) >= 0. && max (q.x, q.y) < 1.) {
    q.x *= mxChar;
    sgn = sign (val);
    val = abs (val);
    if (val < 0.5) sgn = 1.;
    nDig = (val > 0.) ? floor (max (log (val + 0.01) / log (10.), 0.)) + 1. : 1.;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = val / pow (10., mxChar - idChar - 1.);
    if (idChar == mxChar - nDig - 1. && sgn < 0.) s = ShowDig (q, -1);
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 32.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

// "Twisted Curves 2" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

const float txRow = 32.;
const float wScl = 40.;
const float pi = 3.14159;

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 wgBx[3], mPtr, mPtrP, stDat;
  vec3 cvParm;
  vec2 iFrag, canvas, ust;
  float tCur, tCurP, vW, cvOrd, cvWrapI, cvWrapF, asp, el, az;
  int pxId, wgSel, wgReg, kSel;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 3) discard;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / iResolution.xy - 0.5;
  wgSel = -1;
  wgReg = -2;
  asp = canvas.x / canvas.y;
  if (iFrame <= 5) {
    cvParm = vec3 (32., 2., 1.) / wScl;
    mPtrP = mPtr;
    el = 0.7 * pi;
    az = 0.;
    tCurP = tCur;
  } else {
    cvParm = Loadv4 (0).xyz;
    stDat = Loadv4 (1);
    el = stDat.x;
    az = stDat.y;
    tCurP = stDat.z;
    stDat = Loadv4 (2);
    mPtrP = vec4 (stDat.xyz, 0.);
    wgSel = int (stDat.w);
  }
  if (mPtr.z > 0.) {
    wgBx[0] = vec4 (0.35 * asp, 0., 0.012 * asp, 0.18);
    wgBx[1] = vec4 (0.4 * asp, 0., 0.012 * asp, 0.18);
    wgBx[2] = vec4 (0.45 * asp, 0., 0.012 * asp, 0.18);
    for (int k = 0; k < 3; k ++) {
      ust = abs (mPtr.xy * vec2 (asp, 1.) - wgBx[k].xy) - wgBx[k].zw;
      if (max (ust.x, ust.y) < 0.) wgReg = k;
    }
    if (mPtrP.z <= 0.) wgSel = wgReg;
  } else {
    wgSel = -1;
    wgReg = -2;
  }
  if (wgSel < 0) {
    if (mPtr.z > 0.) {
      az += 0.1 * mPtr.x;
      el += 0.1 * mPtr.y;
    } else {
      az += 0.1 * (tCur - tCurP);
      el += 0.07 * (tCur - tCurP);
    }
  } else {
    if (wgSel < 3) {
      for (int k = 0; k < 3; k ++) {
        if (wgSel == k) {
          kSel = k;
          vW = (1. / wScl) * floor (wScl * clamp (0.5 +
             0.5 * (mPtr.y - wgBx[k].y) / wgBx[k].w, 0.01, 0.99));
          break;
        }
      }
      if      (kSel == 0) cvParm.x = vW;
      else if (kSel == 1) cvParm.y = vW;
      else if (kSel == 2) cvParm.z = vW;
    }
  }
  cvOrd = floor (clamp (wScl * cvParm.x + 0.5, 1., wScl));
  cvWrapI = floor (clamp (wScl * cvParm.y + 0.5, 0., wScl - 1.));
  cvWrapF = min (floor (clamp (wScl * cvParm.z + 0.5, 0., wScl - 1.)), cvOrd - 1.);
  cvParm = (1. / wScl) * vec3 (cvOrd, cvWrapI, cvWrapF);
  if (pxId == 0) stDat = vec4 (cvParm, 0.);
  if (pxId == 1) stDat = vec4 (el, az, tCur, 0.);
  else if (pxId == 2) stDat = vec4 (mPtr.xyz, float (wgSel));
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}
