// variant of https://shadertoy.com/view/3s3BW2

float ease( float x ) {                                 // rewritten from https://shadertoy.com/view/3ddBWB
    x = clamp(x, 0.,1.);                                // stepier than smoothstep or .5-.5*cos
	return x > .5 ? x = 1.-x, 1. - 4.* x*x*x
                  :                4.* x*x*x ;
}

#define D \
    V = abs( ( fract(U/2.)-.5)*mat2( cos(3.14/2.*t + vec4(0,11,33,0))) ); \
    O += smoothstep( w, -w, max(V.x,V.y) - .25 ) 

void mainImage( out vec4 O, vec2 u )
{   
    O-=O; 
    vec2 R = iResolution.xy,
         U = u *= 8. / R.y, V;
    if (U.x > 8.) { O += .5; return; }                  // out of board

    float T = iTime/2.,
       // t = ease( 1.5*fract(T) - U.x/16. ),
          t = ease( 1.5*fract(T) - U[int(T)%2]/16. ),
          n = mod(T,2.), w = 4./R.y;

    if (n > 1.)  U.x++, t = 1.-t;
    U -= .5; D;
    U++;     D;
    if (n < 1.)  O = 1.-O;
}
