// optimization of https://shadertoy.com/view/Wscyzr
// simplification + comments of "lorenz11" by mrtkp9993. https://shadertoy.com/view/3tBfWd


void mainImage( out vec4 O, vec2 u )
{    
     vec2 R = iResolution.xy, 
        Min = T(vec2(2,0)).xy,
        Max = T(vec2(2,0)).zw,
          U = ( u - .5*R ) / R.y; U.y += .375;
    U /= VIEW_SCALE;

	O = vec4(.93, .36, .36, 1) * T(u).x; // attention, T(U) goes up to 5 and more

    if (U.x>Min.x && U.y>Min.y && U.x<Max.x && U.y<Max.y) O.b++; // for demo
}

//https://en.wikipedia.org/wiki/Lorenz_system

#define MODE xz                       // choose which 2D projection to draw

float STEPS = 96.,                    // graphics settings
      SPEED = .05,
  INTENSITY = .2,
       FADE = .99,
      FOCUS = 1.,
     O = 10., P = 28., B = 8./3.;     // System Parameters

vec3 start = vec3( .1, .001, 0 );     // Initial Position

vec3 Integrate(vec3 cur, float dt)    // Calculate the next position 
{
	return cur + dt * vec3(    O  * (cur.y - cur.x)       ,
                            cur.x * (P - cur.z) - cur.y   ,
                            cur.x *   cur.y     - B*cur.z   );
}

float Line(vec2 a, vec2 b, vec2 U)    // Distance to a line segment https://www.shadertoy.com/view/llySRh
{   
    U -= a, b -= a;
	float h = dot( U, b ) / dot(b,b),
          c = clamp(h, 0., 1.);
    return h==c ? length( U - b * c ) : 1e5;   // dist to strict segment
  //return        length( U - b * c );         // dist to segment with round ends
}

void mainImage( out vec4 O, vec2 u )
{    
    vec2 R = iResolution.xy, Min,Max,
         U = ( u - .5*R ) / R.y; U.y += .375;
    U /= VIEW_SCALE;
    float pix = 1. / R.y / VIEW_SCALE;
    u = floor(u);

    vec3 last = T(0).xyz,              // pixel (0,0) saves the current position.
         next = last;

    if (u.y==0. && u.x<3. ) {          // --- simulation zone
        Max = Min = last.MODE;
        for(float i = 0.; i < STEPS; i++ )
    	    next = Integrate( next, .016 * SPEED ),
            Min  = min(Min, next.MODE),
            Max  = max(Max, next.MODE);

        O =   u == vec2(0,0)                  // pixel (0,0) saves the current position.
            ? iFrame < 1 ? vec4( start, 0 )          // Setup initial conditions.
                         : vec4( next , 0 )          // Save current position.
            : u == vec2(1,0)                  // pixel (1,0) saves the previous position.
            ? iFrame < 1 ? vec4( start, 0 )          // Setup initial conditions.
                         : vec4( last , 0 )   // pixel (2,0) saves the segment bbox     
            :  /* (2,0) */ vec4( Min-pix , Max+pix );
    } else {                           // --- drawing zone
        Min = T(vec2(2,0)).xy,
        Max = T(vec2(2,0)).zw;
        float c = 0.;
        if (U.x>Min.x && U.y>Min.y && U.x<Max.x && U.y<Max.y) {

            last = T(vec2(1,0)).xyz;   // pixel (0,0) saves the previous position.
            float d = 1e6;

            for(float i = 0.; i < STEPS; i++ )
                next = Integrate( last, .016 * SPEED ),
                d = min(d, Line(last.MODE , next.MODE, U) ),        
                last = next;

            d *=  VIEW_SCALE;
            c  =  INTENSITY / SPEED * smoothstep( FOCUS / R.y, 0., d )
                + INTENSITY / 8.5   * exp(- 1e3 * d*d );
        }
        O =  vec4( c +  T(u) * FADE ); // accumulates draw fading over time.
    }
}
