// Color Mixer
// my step by step tutorial 9
// by Arthur Stammet, 03.2019

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y; // set center to the middle of the screen

    // change values here --------------------------------------------------------------

    // Disk Color-Cycling RGB-Values
    float SpeedDR = iTime * 0.7;
    float SpeedDG = iTime * 0.8;
    float SpeedDB = iTime * 0.9;

    // Background Color-Cycling RGB-Values
    float SpeedBR = iTime * 0.11;
    float SpeedBG = iTime * 0.12;
    float SpeedBB = iTime * 0.13;

    float Speed1 = iTime * 0.51;
    float Speed2 = iTime * 0.55;
    float Sharpness = 10.; // Higher values for sharper edges

    // ---------------------------------------------------------------------------------

	// Animate color cycling for the background
    float BR = 0.5 + sin(SpeedBR*0.5); 	// 0.0 <> 1.0
	float BG = 0.5 + sin(SpeedBG*0.5);	// 0.0 <> 1.0
	float BB = 0.5 + sin(SpeedBB*0.5); 	// 0.0 <> 1.0

	// Animate color cycling for the disk
    float DR = 0.5 - sin(SpeedDR*0.5); 	// 0.0 <> 1.0
	float DG = 0.5 - sin(SpeedDG*0.5);	// 0.0 <> 1.0
	float DB = 0.5 - sin(SpeedDB*0.5); 	// 0.0 <> 1.0

    float radius = 0.45; // radius of the disk ( 0.5 = height of the screen ) 
    float blur = 0.02; // low values for sharper edges

    float Rot1 = Sharpness * sin(Speed1);
    float Rot2 = Sharpness * sin(Speed2);

    vec3 disk = vec3( DR, DG, DB ); // colorized disk (animated)	
    vec3 back = vec3( BR, BG, BB ); // colorized background (animated)	
    vec2 CenterDisk = p - vec2( 0.0, 0.0 );	// center of the disk

    disk *= smoothstep( radius, radius-blur, length(CenterDisk)); // moving disk (animated)
	back *= p.x + Rot1 * p.y;
    back += p.y + Rot2 * p.x;

    fragColor = vec4(back + disk, 1.0);
}
