// radiual chromab in this buffer

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
	vec2 uvn = (fragCoord - 0.5*iResolution.xy)/iResolution.xy;

	fragColor = vec4(0);
    // Radial blur
    float steps = 16.;
    float scale = 0.00 + pow(dot(uvn,uvn),1.1)*0.0;
    float chromAb = pow(length(uv - 0.5),1.5)*2.2;
    vec2 offs = vec2(0);
    vec4 radial = vec4(0);
    for(float i = 0.; i < steps; i++){
        scale *= 0.97;
        vec2 target = uv + offs;
        offs -= normalize(uvn)*scale/steps;
    	radial.r += texture(iChannel0, target + chromAb*1.4/iResolution.xy).x;
    	radial.g += texture(iChannel0, target).y;
    	radial.b += texture(iChannel0, target - chromAb*1./iResolution.xy).z;
    }
    radial /= steps;

    fragColor += radial;

    //fragColor = 1. - fragColor;
    fragColor = mix(fragColor,smoothstep(0.,1.,fragColor), 0.);
    fragColor = max(fragColor, 0.);
    fragColor *= 1. - dot(uvn,uvn)*1.   ;
    fragColor = pow(fragColor, vec4(0.4545 ));
}

#define pi acos(-1.)
#define tau (2.*pi)
#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))

float xch(float d, float b){
	return b < 0. ? 1.5 - d : d;
}

// from https://necessarydisorder.wordpress.com/
float ease(float p, float g) {
    if(p < 0.){
        return 0.;
    } else if(p > 1.){
    	return 1.;
    } else {
        if (p < 0.5) 
            return 0.5 * pow(2.*p, g);
        else
            return 1. - 0.5 * pow(2.*(1. - p), g);
    }
}

float ease(float p, float power, float jump, float offs, float end) {
    float j = p;
    p *= offs;
    float r = (1.-cos(pi*p))/2.*jump;
    r = pow(r, power);
    r = mix(r, 1., pow(smoothstep(0.,1.,j),end));
	return r;
}
float smease(float p, float g) {
    float s = p;
    for(float i = 0.; i < g; i++){
    	s = smoothstep(0.,1.,s);
    }
    return s;
}


float sdBox(vec2 p, vec2 s){
	p = abs(p) - s;
	return max(p.x, p.y);
}


const float speed = 0.65;
float[] scenes = float[3](0.6*speed,1.*speed, 1.11*speed); 

float S = 0.3;
vec3 get( vec2 fragCoord  )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;    
    uv *= 2.;
    float col = 0.;

    float d = 10e6;

    float bA;
    float bB;
    float bC;
    float bD;
    float bE;

    float pl;

    float t = iTime;

    //t += scenes[0];
    t += scenes[1] - 0.1;
    //t += scenes[2] - 0.1;

    uv *= rot(pi*0.25);
    t = mod(t, scenes[0] + scenes[1] + scenes[2]  );

    float offsamt = 0.5;
    float offs = -S*offsamt;
    float offsB = S*(1. - offsamt);

    if(t < scenes[0]){
        //float env = ease(t/scenes[0], 2.);
        float env = ease(t/scenes[0], 1., 1.14, 0.96, 10.);

        float mov = env * offs;
        bA = sdBox(uv - vec2(0.+S + mov,S + mov), vec2(S*1.));
        bB = sdBox(uv + vec2(0.+S + mov,S + mov), vec2(S*1.));
        bC = sdBox(uv - vec2(0.-S - mov,S + mov), vec2(S*1.));
        bD = sdBox(uv + vec2(0.-S - mov,S + mov), vec2(S));
        bE = sdBox(uv, vec2(-offs*(0. + env*1.)));
        col = xch(col,bA);
        col = xch(col,bB);
        col = xch(col,bC);
        col = xch(col,bD);
        col = xch(col,bE);

    } else if(t < scenes[1] + scenes[0]){
        t -= scenes[0];
        float env = ease(t/scenes[1], 4.);

        float mov = env * offs*1.;
        float offsS = 0.*-offs;
        bA = sdBox(uv - vec2(0.+S + offsS + mov,S+ offsS + mov), vec2(offsB));
        bB = sdBox(uv + vec2(0.+S + offsS + mov,S+ offsS + mov), vec2(offsB));
        bC = sdBox(uv - vec2(0.-S - offsS - mov,S+ offsS + mov), vec2(offsB));
        bD = sdBox(uv + vec2(0.-S - offsS - mov,S+ offsS + mov), vec2(offsB));
        bE = sdBox(uv, vec2(-offs + mov));
        col = xch(col,bA);
        col = xch(col,bB);
        col = xch(col,bC);
        col = xch(col,bD);
        col = xch(col,bE);

    }  else if(t < scenes[1] + scenes[0] + scenes[2]){
        t -= scenes[0] + scenes[1];
        float env = ease(t/scenes[2]*1.25, 4.);

        float mov = env * offs*2.;
        uv *= rot(0.5*pi*env);
        bA = sdBox(uv, vec2(offsB*2. - mov));
        col = xch(col,bA);


    } 



    //col += smoothstep(0.00,3./iResolution.y,-d);

    return vec3(col);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    float aa = 5.;

    for(float i =0.; i < aa*aa + min(float(iFrame),0.); i++){
    	col += get(fragCoord + vec2(mod(i,aa),floor(i/aa))/aa);
    }
    col /= aa*aa;

    col = max(col, 0.);

    col = clamp(col, 0., 1.);
    col *= 0.996;
    col = 1. - col;

    fragColor = vec4(col,1.0);
}
