#define hr vec2(1.,sqrt(3.))

vec4 hexagrid(vec2 uv,float detail){
    uv*=detail;
    vec2 ga = mod(uv,hr)-hr*.5;
    vec2 gb = mod(uv-hr*0.5,hr) - hr*.5;
    vec2 guv = (dot(ga,ga) < dot(gb,gb)) ? ga : gb;
    vec2 gid = uv - guv;
    return vec4(guv,gid);
}
mat2 r(float a){
    float c=cos(a),s=sin(a);
    return mat2(c,-s,s,c);
}
float trigrid(vec2 uv) {
   return sign(fract(uv.x+0.5*uv.y)-fract(uv.y));
}


float cyl(vec3 p, float r, float h){

    return max(length(p.xy)-r,abs(p.z)-h);
}
#define pTime floor(iTime*.01) + pow(fract(iTime*.01),.9)
#define qTime floor(-iTime*2.) + pow(fract(-iTime*2.),1.5)
float tunnel(vec3 p){
        p.x += texture(iChannel0,p.xz*.01+vec2(0.,pTime)).x*4.;
        p.y += texture(iChannel0,p.yz*.01+vec2(0.,pTime)).y*2.;

       return -cyl(p+vec3(cos(p.z*.1)*2.,sin(p.z*.1)*2.,0.),10.-p.z*.1,1000000.);
}

float box(vec3 p, vec3 c){
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}
float SDF(vec3 p) {
    vec3 pp = p;
    p.xz *= r(3.141592*.5);

    pp.xy*=r((iTime*.02+pp.z*.05));
    return tunnel(pp);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy-.5* iResolution.xy)/iResolution.y;

    vec3 ro  = vec3(.001,.001,-3.),
    rd = normalize(vec3(uv,1.)),
    p=ro,
    col = vec3(0.);



    float shad = 0.;
    for(float i=0.;i<256.;i++){

       float d = SDF(p);
       if(d <0.001){
        shad = i/64.;
        break;
       }
       p += d*rd*1.1;
    }

    vec4 h = hexagrid(uv,5.);
    vec2 hg = h.xy;
    vec2 hid = h.zw;

    col = vec3(shad);
      vec3 mm = mix(vec3(cos(iTime),.5,.9),vec3(.9,sin(iTime),.1),sin(iTime*.5)*.5+.5);


      float q = abs(tan( (qTime+p.z*.1)*.25));
     col = mix(vec3(.1),vec3(mm.r*q,mm.g*q,mm.b*q),col);
    fragColor = vec4(col,1.0);
}
