vec3 rotate(vec3 p, float angleX, float angleY)
{
    float cosA = cos(angleY);
	float sinA = sin(angleY);
	vec3 r = vec3(p.x, sinA * p.z + cosA * p.y, cosA * p.z - sinA * p.y);
    cosA = cos(angleX);
	sinA = sin(angleX);
	return (-vec3(cosA * r.x - sinA * r.z, r.y, sinA * r.x + cosA * r.z));
}

float flower(vec3 p)
{
    float x2 = p.x * p.x;
    float y2 = p.y * p.y;
    float z2 = p.z * p.z;
	return (1.44 * x2 + y2 + z2 +
            1.0 / sqrt(abs(cos((5.0 + cos(0.5 * iTime)) * atan(p.z / p.y)))) +
    		1.0 / (y2 + z2) + 3.0 / (x2 + y2 + z2) - 7.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
   vec2 aspectRatio = vec2(iResolution.x / iResolution.y, 1.0);
   vec2 uv =  8.0 * aspectRatio * (fragCoord.xy / iResolution.xy - 0.5);

   vec2 mouse = 4.0 * (iMouse.xy / iResolution.xy - 0.5);   
   float d = flower(rotate(vec3(uv.x, uv.y, 2.2), mouse.x+0.5, mouse.y-1.1));

   vec3 color = vec3(0.5 + 0.5 * cos(0.5 * iTime + 2.0),
                     0.5 + 0.5 * sin(0.5 * iTime + 4.0),
                     0.5 + 0.5 * cos(0.5 * iTime + 0.0));

   fragColor = vec4(color * vec3(d/6.0), 1.0);   
}
