#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)
#define pi 3.14159265359
#define Main void mainImage(out vec4 Q, vec2 U) 
#define e(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define N 8.
#define I 10.
vec2 hash22(vec2 p) // Dave H
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
float sg (vec2 p, vec2 a, vec2 b) {
    return  length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));

}

void Y (inout vec4 Q, vec2 U, vec4 n) {
	if ((sg(U,Q.xy,Q.zw)>sg(U,n.xy,n.zw)||length(Q.xy)<3.)&&
        length(n.xy)>3.) Q = n;
}
Main {
    U = round(U);
	Q = A(U);
    Q = round(Q);
    float iN = mod(float(iFrame),N);
    if (iN<.5) {
        if (mod(float(iFrame),N*I)<.5) {
           Q = vec4(R.x*(0.5+0.5*sin(2.61*iTime)),
                    0.25*R.y,
                    R.x*(0.5+0.5*sin(2.61*iTime)),
                    0.);
        } else {
            float
                  n = 1.+mod(floor(float(iFrame)/N/3.),4.),
                  n2 = 1.+mod(floor(float(iFrame+124231)/N/3.),3.),
                  s = mod(floor(U.x+R.x*U.y),n),
                  l = length(Q.zw-Q.xy),
                  a = .3*sin(l+float(iFrame))*
                	  (s-n2+ceil(0.5*n))*pi;
            vec2 h = .1*l*(hash22(floor(Q.xy))*2.-1.);
            vec2 t = Q.xy,
                 g = sin(.5*iTime)*vec2(0,0.0001*R.y*(-0.6*N+iN)*abs((-0.6*N+iN)));
            Q.xy = Q.xy - 0.7*(Q.zw-Q.xy)+.2*(h+g);
            Q.xy = t+(Q.xy-t)*e(a);
        	Q.zw = t;
        }
    }
    else {
        float k = exp2(N-1.-mod(float(iFrame),N));
    	Y(Q,U,A(U+vec2(0,k)));
    	Y(Q,U,A(U+vec2(k,0)));
    	Y(Q,U,A(U-vec2(0,k)));
    	Y(Q,U,A(U-vec2(k,0)));
    }
}

Main {
	vec4 a = A(U);
    float l = length(a.xy-a.zw);
    if (length(a.xy)<3.||length(a.zw)<3.){Q = B(U); return;}
    float r = sg(U,a.zw,a.xy),r1=sg(U+1.,a.zw,a.xy);
    Q = (0.8+0.6*sin(.01*float(iFrame)+mod(float(iFrame),N*I)/N/I*(vec4(1,2,3,4))));
    float x = exp(-.1/(1.+.01*l*l)*r*r*r*r),
    	  y = exp(-.05/(1.+.01*l*l)*r1*r1*r1*r1);
    vec4 b = B(U);
    vec4 m = 0.25*(B(U+vec2(0,1))+B(U+vec2(1,0))+B(U-vec2(0,1))+B(U-vec2(1,0)));
    b = mix(b,m,0.05);
    Q = mix(b*(1.-0.5*y),Q*x,0.5*x);
    Q = min(Q,vec4(1));
    if (iFrame < 1) Q = vec4(0);

}
