void mainImage( out vec4 Q, in vec2 U )
{
    vec2 R = iResolution.xy;
    float s = 0.,sp = 0.;
    for (float i = -30.; i < 30.; i++) {
        vec2 c = vec2(0.8,0.5)*R + vec2(1,2.*i);
        float r = length(U-c);
        vec2 obj = vec2(0.2+0.1*sin(iTime),0.48+0.1*cos(iTime))*R;
       	float r2 = length(obj-c)+length(obj-U);
    	s += sin (r + float(i)*sin(iTime) -10.*iTime)*exp(-.01*i*i)/r/r;
    	sp += max(0.,dot(normalize(obj-c),normalize(obj-U)))*sin (r2 + float(i)*sin(iTime) -10.*iTime)*exp(-.001*i*i)/r2/r2;
    }


    Q = 0.5+0.5*(sin(log(1.+5e-1*log(1.+5e4*max(0.,sp+s)))*vec4(1,2,3,4)));
}
