float Circle(vec2 uv,vec2 p,float r,float blur){
    float d = length(uv - p);
    float c = smoothstep(r,r - blur,d);

    return c ;
}

float Smiley(vec2 uv,vec2 p,float size){

    float s = size;

    uv -= p;
    uv /= size;

    float mask = Circle(uv,vec2(0,0),0.4,0.01);
    mask -= Circle(uv,vec2(-0.13,0.2),0.07,0.01);
    mask -= Circle(uv,vec2(0.13,0.2),0.07,0.01);

    float mouth = Circle(uv,vec2(0,0),0.3,0.01);
    mouth -= Circle(uv,vec2(0,0.1),0.3,0.01);
    mask -= mouth;


	return mask;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -= 0.5;

    uv.x *= iResolution.x/iResolution.y;

    float a = iTime * 0.1;
    if(a > 1.0){
        a = 0.;
    }
    //float a = 0.0;

    //a += (iTimeDelta + 1.0)  ;
    //float b = 0.0;

    //b += (iTime - (a - 0.1)) * 0.1;


    a += 0.0001 * iTime;
    float mask = Smiley(uv,vec2( 0.0,0.0),a);


    //float mask = Smiley(uv,vec2( 0.5 * sin(iTime),0.),0.5 * sin(iTime) );

    //mask +=  Smiley(uv,vec2(0.2,0.2),0.2);
    //mask +=  Smiley(uv,vec2(0.25,-0.2),0.25);
    //mask +=  Smiley(uv,vec2(-0.2,-0.2),0.15);


    vec3 col = vec3(1,1,0);
    col *= mask;

    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}
