void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    float w1 = 41.0;
    float w2 = 23.0;
    float speedMultiplier = 18.0f;

    vec3 col = vec3(0.0f,0.0f,0.0f);
    float d = 0.0f;
    int modular = 10;
    float moveOffset = iTime/speedMultiplier;


    int uvY100 = int(round((uv.y + moveOffset) * 100.0));
    int aStepY100 =  uvY100 % modular;

    float aStepY = (float(aStepY100)/float(modular - 1));
    float dStepY = round(aStepY);


    int uvX100 = int(round((uv.x + moveOffset) * 100.0));
    int aStepX100 =  uvX100 % modular;
    float aStepX = (float(aStepX100)/float(modular - 1));
    float dStepX = round(aStepX);


    float dStepXY = dStepX * dStepY;

    col = vec3(dStepX,dStepX,dStepX);

    float blockHeight = dStepXY/2.0;
    float xRipple = 0.5 * cos((uv.x + iTime/w1) * iTime/(w2*10.0));
    // float yRipple = 0.5 * sin((uv.y + iTime/w2) * iTime/(w1*10.0));
	float yRipple = 0.5 * sin((iTime/(w1*100.0)));

    d = (xRipple + yRipple) * blockHeight;
    // d = blockHeight * (1.0 - uv.x) * (1.0 - uv.y);
    // d = blockHeight * (1.0 - length(uv - 0.5) * 2.0);
    // d = blockHeight;


    fragColor = vec4(col, d);
}

