#define PI      3.14159265
#define TWO_PI  6.28318530
#define HALF_PI 1.57079633

vec3 circle(vec2 uv, float rad, float i){
    float d  = length(uv);
    float a  = atan(uv.y, uv.x);
    vec3 c = vec3(1.);
    vec3 colmult;

    if(i == 0.) colmult = vec3(1., 0., 0.);
    if(i == 1.) colmult = vec3(0., 1., 0.);
	if(i == 2.) colmult = vec3(0., 0., 1.);


    rad += 0.05*cos(5.*a - i*HALF_PI + 3.*iTime)*pow(.5+.5*cos(iTime - a), 6.);
    c -= 1.-smoothstep(rad, rad+0.01, d) + smoothstep(rad*.93, rad*.93+0.01, d);

    return c * colmult;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(1.);
    float rad = 0.4;

    col += circle(uv, rad, 0.);
    col += circle(uv, rad, 1.);
    col += circle(uv, rad, 2.);


    fragColor = vec4(col, 1.0);
}
