#define ITER 100.0
#define STEP_TIME 0.1
#define SCALE 0.45
const vec2 center = vec2(0.5, 0.0);

const vec3 color1 = vec3(1.0, 0.0, 0.0);
const vec3 color2 = vec3(0.0, 0.0, 1.0);

void mainImage( out vec4 fragColor,  vec2 uv ) {
	uv /= iResolution.xy;
	vec2 c = vec2(4, 3) * (uv - .5) / (SCALE * 3.0) - center, z = c;

    fragColor -= fragColor;

    float v = 0.0;

    for(float i=0.; i < (ITER / 100.0); i+=.01) 
        if( length(z = mat2(z, -z.y, z.x) * z + c) > 2.) { 
            v += 1.0 - i;
            break;
        }

    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));
    fragColor = mix(vec4(1.0 - col, 1.0), vec4(col, 1.0), v);

}
