void mainImage(out vec4 o,vec2 u){
    o = cos(vec4(0,2,4,0)+(u.xyyy/iResolution.xyyy)+iTime)/2.+.5;
    vec4 ot = texelFetch(iChannel0,ivec2(u),0);
    o = mix(clamp(o,0.,1.),ot,ot.w);
}

float line(vec2 pos,vec2 p1,vec2 p2){
    p1 = p1-(p2-p1)*10.;
    return length(mix(p1,p2,clamp(length(pos-p1)/length(p2-p1),0.9095,1.))-pos);
}

vec4 value(vec2 u,vec2 pos,int mem,vec2 p){
    float r = iResolution.y/4.;
    float l = line(u,pos-vec2(0,0.1),pos+vec2(0,0.1))*r-r/40.;
    float v = texelFetch(iChannel0,ivec2(mem,0),0).x;
    float lb = line(u,pos-vec2(0,0.1),pos-vec2(0,.1)+vec2(0,0.1)*v*2.)*r-r/40.;
    vec4 o = vec4(0);float t = 0.;
    t = clamp(1.-lb,0.,1.);
    t = max(t,clamp(1.-l,0.,.75));
    t = max(t,clamp(1.-l+r/160.,0.,.5));
    o.w = t;
    float m = 2.;
    o.xyz = mix(vec3(1),
                vec3(0),
                clamp(1.-(lb*2.),0.,1.)
    );
    if(int(p.y)==0)o=vec4(0);
    if(int(p.y)==0&&ivec2(p)==ivec2(mem,0)){
        o.x=texelFetch(iChannel0,ivec2(mem,0),0).x;
        vec2 mv = (iMouse.xy*2.-iResolution.xy)/iResolution.y;
        if(iMouse.z>.0&&mv.x>pos.x-.03&&mv.x<pos.x+.03&&mv.y>pos.y-.12&&mv.y<pos.y+.12)o.x=clamp((mv.y-pos.y+.1)/.2,0.,1.);
    }
    return o;
}

void mainImage(out vec4 o,vec2 u){
    o = vec4(0);
    vec2 uv = (u+u-iResolution.xy)/iResolution.y;
    float ch = iResolution.x/iResolution.y;
    vec4 ot = vec4(0);
    ot = max(ot,value(uv,vec2(-.97, .08)*ch,0,u));
    ot = max(ot,value(uv,vec2(-.92, .08)*ch,1,u));
    ot = max(ot,value(uv,vec2(-.87, .08)*ch,2,u));
    ot = max(ot,value(uv,vec2(-.97,-.08)*ch,3,u));
    ot = max(ot,value(uv,vec2(-.92,-.08)*ch,4,u));
    ot = max(ot,value(uv,vec2(-.87,-.08)*ch,5,u));
    o = ot;
    o.w = clamp(o.w,0.,1.);
}
