void mainImage( out vec4 O, vec2 U )
{
#if 0
    U += .00001*iTime; // micro-variations 
#else
    U = floor(U);      // U integer
#endif

    if (min(U.x,U.y)<10.) { O = mod(max(U.x,U.y),10.)==0. ? vec4(0,0,1,1):vec4(1); return;}

    U /= U + min(0.,iTime); // hack because windows optimizer is smart ;-)
    O.rg = vec2(U.x==1., U.y==1.);

}
