    // MengerSponge Part 2: Slimmed Bounded Cross
    //dist = sdBoundedCrossSlimmed((rayPos - mengerSpongePos) / 1.5) * 1.5;

    // MengerSponge Part 3: Bounded Cross w/ Box
    //dist = sdBoundedCrossWithBox((rayPos - mengerSpongePos) / 1.5) * 1.5;

    // MengerSponge Part 4: Iteration 1
    //dist = sdMengerSpongeIteration1((rayPos - mengerSpongePos) / 1.5) * 1.5;

    // MengerSponge Part 5: Bounded Box Field (Broken)
    //dist = sdBoundedBoxFieldBroken((rayPos - mengerSpongePos) / 0.5) * 0.5;

    // MengerSponge Part 6: Bounded Box Field (Fixed)
    //dist = sdBoundedBoxField((rayPos - mengerSpongePos) / 0.5) * 0.5;

    // MengerSponge Part 7: Twenty Seven Boxes...Kinda
    //dist = sdTwentySevenBoxesKinda((rayPos - mengerSpongePos) / mengerSpongeScale) * mengerSpongeScale;

    // MengerSponge Part 8: Twenty Seven Boxes Cross-Section
    //dist = sdTwentySevenBoxesCross((rayPos - mengerSpongePos) / mengerSpongeScale) * mengerSpongeScale;

    // MengerSponge Part 9: 27 Cubes
    //dist = sdTwentySevenBoxes((rayPos - mengerSpongePos) / mengerSpongeScale) * mengerSpongeScale;

