// NOTE: Entry point of fragment shader
// This function point is called per pixel
// fragCoord is (x,y) location of pixel
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
    // Subtract half the width/height resolution components to
    // Move origin, (0,0), from bottom left corner to center of screen
    vec2 pixelCoord = fragCoord.xy - (iResolution.xy * 0.5);

    // Scale y value to [-1.0, 1.0], scale x by same factor (if screen is wider, x will have values >1 & <-1)
    pixelCoord = pixelCoord / iResolution.y;

    // Represents the fragment position on the plane where rays are shot through
    // Can be thought of as the pixel coordinate on the screen
    vec3 fragmentPos = vec3(pixelCoord.x, pixelCoord.y, 0.0);

    // The point in which our rays emit from
    // Adjusting z value in the range of [-3.0, 0.0] gives a variety of viewing angles
    const vec3 focalPoint = vec3(0.0, 0.0, -1.0);

    // get the direction of a ray emitting from the focal point through fragment position
    vec3 rayDir = fragmentPos - focalPoint;

    // rotate direction vectors to point towards the world origin (where our objects will be centered)
    // these "magic" numbers were derived from the angle needed to point from cameraPos to the world origin
    rayDir.yz *= rotate(radians(35.24)); 
    rayDir.xz *= rotate(radians(45.0));
    rayDir = normalize(rayDir);

    vec2 dist = distanceRayToScene(rayDir);

    if(dist.x > 0.0) { // hit
        // color surface by number of iterations (from white to black)
        vec3 col = vec3(1.0 - (dist.y/float(MAX_STEPS)));
        fragColor = vec4(col, 1.0);
    } else { // miss
        fragColor = vec4(missColor, 1.0);
    }
}


// returns vec2(dist, iterations)
// NOTE: ray dir arguments are assumed to be normalized
vec2 distanceRayToScene(vec3 rayDir) {

	float dist = 0.0;

	for(int i = 0; i < MAX_STEPS; i++) {
        vec3 pos = cameraPos + (dist * rayDir);
        float posToScene = distPosToScene(pos);
        dist += posToScene;
        if(abs(posToScene) < HIT_DIST) return vec2(dist, i); // absolute value for posToScene incase the ray makes its way inside an object
        if(posToScene > MISS_DIST) break;
    }

    return vec2(-1.0f, MAX_STEPS);
}

float sdSphere(vec3 rayPosition, vec3 sphereCenterPosition, float radius) {
    vec3 centerToRay = rayPosition - sphereCenterPosition;
	float distToCenter = length(centerToRay);
    return distToCenter - radius;
}

float sdSphere(vec3 rayPos, float radius) {
    return length(rayPos) - radius;
}

float sdSphere(vec3 rayPos) {
    return length(rayPos) - HALF_WIDTH;
}

float sdCube(vec3 rayPos) {
	const vec3 corner = vec3(HALF_WIDTH);
    vec3 ray = abs(rayPos); // fold ray into positive octant
    vec3 cornerToRay = ray - corner;
    float cornerToRayMaxComponent = max(max(cornerToRay.x, cornerToRay.y), cornerToRay.z);
    float distToInsideRay = min(cornerToRayMaxComponent, 0.0);
    vec3 closestToOutsideRay = max(cornerToRay, 0.0);
	return length(closestToOutsideRay) + distToInsideRay;
} 

float sdCross(vec3 rayPos) {
    const vec3 corner = vec3(HALF_WIDTH);
	vec3 ray = abs(rayPos); // fold ray into positive quadrant
	vec3 cornerToRay = ray - corner;

    float smallestComp = min(min(cornerToRay.x, cornerToRay.y), cornerToRay.z);
	float largestComp = max(max(cornerToRay.x, cornerToRay.y), cornerToRay.z);
	float middleComp = cornerToRay.x + cornerToRay.y + cornerToRay.z
							- smallestComp - largestComp;

	vec2 closestOutsidePoint = max(vec2(smallestComp, middleComp), 0.0);
	vec2 closestInsidePoint = min(vec2(middleComp, largestComp), 0.0);

	return (middleComp > 0.0) ? length(closestOutsidePoint) : -length(closestInsidePoint);
}

float sdBoundedCross(vec3 rayPos) {
	float boundingBoxDist = sdCube(rayPos / 2.0) * 2.0;
	float crossDist = sdCross(rayPos);
	float intersection = max(boundingBoxDist, crossDist);
	return intersection;
}


float sdBoundedCrossSlimmed(vec3 rayPos) {
	float boundingBoxDist = sdCube(rayPos / 2.0) * 2.0;
	float crossDist = sdCross(rayPos / 0.33) * 0.33;
	float intersection = max(boundingBoxDist, crossDist);
	return intersection;
}


float sdBoundedCrossWithBox(vec3 rayPos) {
	float boundingBoxDist = sdCube(rayPos / 2.0) * 2.0;
	float crossDist = sdCross(rayPos * 3.0) / 3.0;
	float intersection = max(boundingBoxDist, crossDist);
	float spongeBox = sdCube(rayPos);
	float boxCrossUnion = min(intersection, spongeBox);
	return boxCrossUnion;
}


float sdMengerSpongeIteration1(vec3 rayPos) {
	float crossDist = sdCross(rayPos * 3.0) / 3.0;
	float spongeBox = sdCube(rayPos);
	return max(spongeBox, -crossDist);
}


float sdBoundedBoxFieldBroken(vec3 rayPos) {
	float boundingBox = sdCube(rayPos / 3.9) * 3.9;
	vec3 ray = mod(rayPos, 2.0);
	float dist = sdCube(ray / 1.5) * 1.5;
	return max(dist, boundingBox);
}


float sdBoundedBoxField(vec3 rayPos) { 
	float boundingBox = sdCube(rayPos / 4.0) * 4.0;
	vec3 ray = mod(rayPos, 2.0);
	ray -= 1.0;
	float dist = sdCube(ray / 0.8) * 0.8;
	return max(dist, boundingBox);
}

float sdTwentySevenBoxesKinda(vec3 rayPos) {
	// bounding box spans all axes between [-halfBoxDimen, halfBoxDimen]
	float boundingBox = sdCube(rayPos);
	// The rays are transformed into boxed spaces with all axes between [0, boxDimen / 3.0)
	// this is because each cube should have a third the dimension of the entire cube
	float boxedWorldDimen = WIDTH / 3.0;
	vec3 ray = mod(rayPos, boxedWorldDimen);
	// The origin within the boxes is moved from the corner to the center of the cube,
	// with the rays now having a range between [-boxDimen / 6.0, boxDimen / 6.0)
	ray -= (boxedWorldDimen) / 2.0;
	// Transform coordinates of each boxed space to range [-halfBoxDimen, halfBoxDimen)
	ray *= 3.0;
	// Shrink the cubes slightly to reveal gaps between them
	float dist = sdCube(ray / 0.9) / 0.9;
	// Correct warping of space caused by previously stretching the ray
	dist /= 3.0;
	return max(dist, boundingBox);
}

float sdTwentySevenBoxesCross(vec3 rayPos) {
	float boundingBox = sdCube(rayPos);
	float boxedWorldDimen = WIDTH / 3.0;
	vec3 ray = mod(rayPos, boxedWorldDimen);
	ray -= (boxedWorldDimen) / 2.0;
	ray *= 3.0;
	float dist = sdCube(ray / 0.9) * 0.9;
	dist /= 3.0;
	dist = max(dist, boundingBox);
	// Cross that runs along the axes of our world
	float axesCross = sdCross(rayPos / 0.25) * 0.25;
	return max(dist, axesCross);
}

float sdTwentySevenBoxes(vec3 rayPos) {
	float boundingBox = sdCube(rayPos);
	float boxedWorldDimen = WIDTH / 3.0;
	// We want to translate the first box in the positive octant to change it’s world coordinates
	// from [0, boxedWorldDimen) to [-boxedWorldDimen / 2.0, boxedWorldDimen / 2.0) 
	float translation = boxedWorldDimen / 2.0;
	vec3 ray = rayPos + translation;
	ray = mod(ray, boxedWorldDimen);
	ray -= (boxedWorldDimen) / 2.0;
	ray *= 3.0;
	float dist = sdCube(ray / 0.9) * 0.9;
	dist /= 3.0;
	return max(dist, boundingBox);
}

float sdTwentySevenCrossesBound(vec3 rayPos) {
    const float oneThird = 1.0 / 3.0;
	float boundingBox = sdCube(rayPos);
	float boxedWorldDimen = WIDTH / 3.0;
	float translation = boxedWorldDimen / 2.0;
	vec3 ray = rayPos + translation;
	ray = mod(ray, boxedWorldDimen);
	ray -= (boxedWorldDimen) / 2.0;
	ray *= 3.0;
	float dist = sdCross(ray / oneThird) * oneThird;
	dist /= 3.0;
	return max(dist, boundingBox);
}

float sdMengerSpongeIteration2(vec3 rayPos) {
    const float oneThird = 1.0 / 3.0;
	float boxedWorldDimen = WIDTH / 3.0;
	float translation = boxedWorldDimen / 2.0;
	vec3 ray = rayPos + translation;
	ray = mod(ray, boxedWorldDimen);
	ray -= (boxedWorldDimen) / 2.0;
	ray *= 3.0;
	float crossesUnboundDist = sdCross(ray / oneThird) * oneThird;
	crossesUnboundDist /= 3.0;

	float distFirstIter = sdMengerSpongeIteration1(rayPos);
	return max(distFirstIter, -crossesUnboundDist);
}

float sdMengerSponge(vec3 rayPos, int numIterations) {
	float spongeCube = sdCube(rayPos);
    if(spongeCube > HIT_DIST) return spongeCube; // use dist of sponge box as bounding box
	float mengerSpongeDist = spongeCube;

	float scale = 1.0;
	for(int i = 0; i < numIterations; ++i) {
    	float boxedWidth = WIDTH / scale;
    	vec3 ray = mod(rayPos + boxedWidth / 2.0, boxedWidth);
    	ray -= boxedWidth / 2.0;
    	ray *= scale;
    	float crossesDist = sdCross(ray * 3.0);
    	scale *= 3.0;
    	crossesDist /= scale;
    	mengerSpongeDist = max(mengerSpongeDist, -crossesDist);
	}

	return mengerSpongeDist;
}

float sdYAlignedPlane(vec3 rayPos) {
    return abs(rayPos.y);
}

mat2 rotate(float angle) {
    float sine = sin(angle);
    float cosine = cos(angle);
    return mat2(cosine, -sine, sine, cosine);
}
