// "Toroidal Mondrian" by dr2 - 2019
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float PrTorusDf (vec3 p, float ri, float rc);
vec2 Rot2D (vec2 q, float a);
vec2 Hashv2f (float p);
vec4 Loadv4 (int idVar);

#define MAX_DISK 256

vec3 ltDir;
float dstFar, tCur, ringRad;
const float pi = 3.14159;

float ObjDf (vec3 p)
{
  return PrTorusDf (p, 0.5, ringRad);
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 stDat;
  vec3 col, rCol, vn;
  vec2 rcSize, uf, p, r, t, dr;
  float dstObj, bFac, bLen, d, c, nDotL;
  int nDisk;
  bool bCross;
  ringRad = 1.;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    stDat = Loadv4 (MAX_DISK);
    nDisk = int (stDat.x);
    bFac = stDat.y;
    bLen = bFac * floor (sqrt (float (nDisk) + 0.01));
    col = vec3 (1.);
    uf = 0.5 * bLen * vec2 (atan (ro.z, ringRad - length (ro.xy)), atan (ro.y, - ro.x)) / pi;
    for (int n = 0; n < MAX_DISK; n ++) {
      if (n == nDisk) break;
      p = Loadv4 (n).xy;
      rcSize = 0.5 + 1.5 * Hashv2f (float (n + 1));
      r = uf - p;
      d = max (abs (r.x) - rcSize.x, abs (r.y) - rcSize.y);
      t = abs (r) - bLen + rcSize;
      dr = bLen * sign (p);
      bCross = true;
      if (min (t.x, t.y) > 0.) r += dr;
      else if (t.x > 0.) r.x += dr.x;
      else if (t.y > 0.) r.y += dr.y;
      else bCross = false;
      if (bCross) d = min (d, max (abs (r.x) - rcSize.x, abs (r.y) - rcSize.y));
      c = Hashv2f (37.3 * float (n + 1)).x;
      if (c < 0.22) rCol = vec3 (1., 0., 0.);
      else if (c < 0.44) rCol = vec3 (0., 1., 0.);
      else if (c < 0.66) rCol = vec3 (0., 0., 1.);
      else if (c < 0.88) rCol = vec3 (1., 1., 0.);
      else rCol = vec3 (1.);
      col = mix (rCol * (1. - step (-0.1, d)), col, step (0., d)); 
    }
    nDotL = max (dot (vn, ltDir), 0.);
    nDotL *= nDotL;
    col = col * (0.25 + 0.65 * nDotL * nDotL) +
       0.1 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  } else col = vec3 (0.95, 0.95, 1.) * (0.1 + 0.05 * rd.y);

  return clamp (col, 0., 1.);
}

#define AA  1 

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az -= 0.05 *  pi * tCur;
    el -= 0.037 *  pi * tCur;
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -6.);
  dstFar = 20.;
  ltDir = vuMat * normalize (vec3 (0.2, 0.3, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), 3.3));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

// "Toroidal Mondrian" by dr2 - 2019
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float Hashff (float p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

#define MAX_DISK 256

const int nDisk = 256;
const float pi = 3.14159;
float bFac;

vec4 Step (int mId)
{
  vec4 p;
  vec2 dr, f;
  float fOvlap, rSep, bLen, dt;
  bLen = bFac * floor (sqrt (float (nDisk) + 0.01));
  f = vec2 (0.);
  p = Loadv4 (mId);
  fOvlap = 1000.;
  for (int n = 0; n < nDisk; n ++) {
    dr = p.xy - Loadv4 (n).xy;
    if (abs (dr.x) > 0.5 * bLen) dr.x -= bLen * sign (dr.x);
    if (abs (dr.y) > 0.5 * bLen) dr.y -= bLen * sign (dr.y);
    rSep = length (dr);
    if (n != mId && rSep < 1.) f += fOvlap * (1. / rSep - 1.) * dr;
  }
  dt = 0.005;
  p.zw += dt * f;
  p.xy += dt * p.zw;
  p.xy -= bLen * sign (p.xy) * step (vec2 (0.5 * bLen), abs (p.xy));
  return p;
}

vec4 Init (int mId)
{
  vec4 p;
  float x, y;
  int nDiskEdge;
  nDiskEdge = int (sqrt (float (nDisk) + 0.01));
  y = float (mId / nDiskEdge);
  x = float (mId) - float (nDiskEdge) * y;
  p.xy = bFac * ((vec2 (x + 0.25 * (2. * mod (y, 2.) - 1.), y) - 0.5 * float (nDiskEdge - 1)));
  p.zw = 3. * sin (2. * pi * Hashff (float (mId)) + vec2 (0.5 * pi, 0.));
  return p;
}

const float txRow = 128.;

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat, p;
  vec2 iFrag;
  int mId;
  bool init;
  iFrag = floor (fragCoord);
  mId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || ! (mId < nDisk || mId == MAX_DISK)) discard;
  init = (iFrame <= 5);
  if (init) {
    bFac = 1.5;
    stDat = vec4 (float (nDisk), bFac, 0., 0.);
  } else {
    stDat = Loadv4 (MAX_DISK);
    ++ stDat.z;
    bFac = stDat.y;
  }
  if (mId < nDisk) {
    if (init) p = Init (mId);
    else p = Step (mId);
  }
  Savev4 (mId, ((mId < nDisk) ? p : stDat), fragColor, fragCoord);
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}
