#define STEPS 10.
#define BLACK_COL vec3(23,32,38)/255.

#define sinp(v) (sin(v)*.5+.5)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

    float t = iTime * .1;       
    float ca = cos(t);
    float sa = sin(t);
    mat2 rot = mat2(ca, -sa, sa, ca);

    float m = 0.;
    vec3 col = vec3(0.);          
    float sStep = 1./STEPS;
    float l = length(uv);
    for(float i = STEPS; i>0.; i-=1.){
        vec2 iuv = uv*(1.0 + sinp(iTime*.5)*.5 + i*.1) + vec2(ca, sa);        		
        iuv*=rot;

        vec2 guv = iuv * 5.;        
        guv = fract(guv);

        float bSize = .15 - i*.0075;
        float sm = 1. - step(bSize, guv.x) * step(bSize, guv.y);  
        if(sm > 0.){
            col = vec3(1.1 - sStep * i - l);
        }

        m += sm;
    }       

    col = mix(BLACK_COL, col, clamp(m, 0., 1.));    
    fragColor = vec4(col,1.0);
}
