void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 Colors[4];

    Colors[0] = vec4(1.0,0.0,0.0,1.0); // Upper left.
    Colors[1] = vec4(0.0,1.0,0.0,1.0); // Lower left.
    Colors[2] = vec4(0.0,0.0,1.0,1.0); // Upper right.
    Colors[3] = vec4(1.0,0.5,0.0,1.0); // Lower right.

    // Normalized pixel coordinates (from 0 to 1)
    vec2 UV = fragCoord/iResolution.xy;
    UV.t = 1.0-UV.t;

    // Direct barycentric.
    vec4 BarycentricResult;
    if ( (1.0-UV.s)>UV.t ) // Upper left.
    {
        float Areas[3];
        Areas[0] = 0.5*length(cross(vec3(1.0,0.0,0.0),vec3(UV,0.0)));
        Areas[1] = 0.5*length(cross(vec3(0.0,1.0,0.0),vec3(UV,0.0)));
        Areas[2] = 0.5-Areas[0]-Areas[1];

        float Weights[3];       
        Weights[0] = Areas[0]/0.5;
        Weights[1] = Areas[1]/0.5;
        Weights[2] = Areas[2]/0.5;

    	BarycentricResult = Weights[0]*Colors[2]+Weights[1]*Colors[1]+Weights[2]*Colors[0];
    }
    else // Lower right.
    {
        float Areas[3];
        Areas[0] = 0.5*length(cross(vec3(1.0,0.0,0.0),vec3(vec2(1.0)-UV,0.0)));
        Areas[1] = 0.5*length(cross(vec3(0.0,1.0,0.0),vec3(vec2(1.0)-UV,0.0)));
        Areas[2] = 0.5-Areas[0]-Areas[1];

        float Weights[3];       
        Weights[0] = Areas[0]/0.5;
        Weights[1] = Areas[1]/0.5;
        Weights[2] = Areas[2]/0.5;

    	BarycentricResult = Weights[0]*Colors[1]+Weights[1]*Colors[2]+Weights[2]*Colors[3];
    }

    // Emulated barycentric.
    vec4 LinearResult;
    if ( (1.0-UV.s)>UV.t ) // Upper left.
    {
        // Geometry shader would supply this fake vertex for linear interpolation over adjacency information.

        // Replace lower right color with additive inverse of upper left color.
        Colors[3] = -Colors[0];

        // Now set lower right colors alpha to 0, so we have a of missing weight we need to normalize for.
        Colors[3].a = 0.0;

        // Fragment shader part would start here.

        // Two dimensional linear interpolation as usual.
    	LinearResult = mix(mix(Colors[0],Colors[1],UV.s),mix(Colors[2],Colors[3],UV.s),UV.t);

        // This is counter intuitive. --han
        LinearResult += (1.0-LinearResult.a)*(Colors[1]+Colors[2]);
    }
    else // Lower right.
    {
        Colors[0] = -Colors[3];
        Colors[0].a = 0.0;

    	LinearResult = mix(mix(Colors[0],Colors[1],UV.s),mix(Colors[2],Colors[3],UV.s),UV.t);

        LinearResult += (1.0-LinearResult.a)*(Colors[1]+Colors[2]);

        // Test match hack.
        //LinearResult = BarycentricResult;
    }

    fragColor = mix(LinearResult,BarycentricResult,pow(0.5+0.5*cos(2.0*iTime),4.0));
}
