void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}

#define PLOT_RES 30.0

// Hue to RGB function from Fabrice's shadertoyunofficial blog:
#define hue2rgb(hue) 0.6 + 0.6 * cos(6.3 * hue + vec3(0.0, 23.0, 21.0))

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}

vec2 Bezier(in vec2 a, in vec2 b, in vec2 c, in vec2 d, in float t) {
    float tInv = 1.0 - t;
    return a * tInv * tInv * tInv * tInv +
           b * 9.0 * t * tInv * tInv * tInv +
           c * 9.0 * tInv * t * t * t +
           d * t * t * t * t;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 50.0 / iResolution.y;
    vec3 color = vec3(0.0);

    float time = iTime * 3.0;

    float t1 = 0.5 * time, t2 = time, t3 = 1.25 * time;
    float c1 = cos(t1), s1 = sin(t1);
    float c2 = cos(t2), s2 = sin(t2);
    float c3 = cos(t3), s3 = sin(t3);

    vec2 a = vec2(c1      * 3.0, s2 * c3 * 2.0);
    vec2 b = vec2(s3 * c2 * 3.0, c2      * 2.0);
    vec2 c = vec2(c3 * c2 * 3.0, s2 * s1 * 2.0);
    vec2 d = vec2(c2 * c1 * 3.0, s3      * 2.0);

    vec2 prevPos = a;
    float tStep = 1.0 / PLOT_RES;
    for (float t=tStep; t < 1.0 + tStep; t += tStep) {
        vec2 curPos = Bezier(a, b, c, d, t);
        color = mix(color, hue2rgb(t + time), smoothstep(unit, 0.0, sdLine(uv, prevPos, curPos)));
        prevPos = curPos;
    }

    fragColor = vec4(color * 2.0, 1.0);
}

#define MOTION_BLUR 0.9

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = mix(texture(iChannel0, uv), texture(iChannel1, uv), 1.0 - MOTION_BLUR);
}
