// sinc variant of https://shadertoy.com/view/WlfSzS

#define SCALE 6.
#define s 5.      // sinc window size = (2s+1)^2
#define sinc(x) sin(3.1416*(x)) / (3.1416*(x)+1e-20)

void mainImage( out vec4 O, vec2 U )
{
    if ( U.x < .4*iResolution.x )
    { O = texelFetch(iChannel0, ivec2(U), 0).xxxx; return; }

    U = (U)/SCALE;
    float v = 0., t = 0.;

    v = 0.; 
    for( float y=-s; y<=s; y++)
        for( float x=-s; x<=s; x++) {
            vec2 P = vec2(x,y), 
                 W = sinc(P-fract(U));
            t += W.x*W.y;
            v += W.x*W.y * texelFetch(iChannel0, ivec2(U+P), 0).x;
        }
    v /= t;

    float p = fwidth(v); //.75/SCALE;
    v = smoothstep(-p,p,v-.5);
  //v = smoothstep(1.5*p,0.,abs(v-.5)); // cool variant
  //v = .5+.5*cos( 6.28/4.*(v-.5)/p );  // cool variant

    O = vec4( pow( v, 1./2.2 ) );
}
