#define rot(j) mat2(cos(j),-sin(j),sin(j),cos(j))
float sdBox(vec3 p, vec3 s){p = abs(p) - s; return max(p.x,max(p.y,p.z));}

#define pmod(p,j) mod(p - 0.5*j,j) - 0.5*j 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    uv*=3.;

    uv.xy += vec2(sin(iTime)/6.,cos(iTime)/5.);

    vec3 col = vec3(0.4,0.6,0.5)/1.;


    vec3 p = vec3(uv,1.);
    #define pi acos(-1.)

    p.yx *= rot(0.0*pi );

    p.yz *= rot(0.25*pi + 0.  - iTime*0.02);

    p.xz *= rot(0.25*pi + sin(iTime)/20.);


    p /= dot(p,p);


    p.x += iTime/20.;
    /*
    p.x -=  iTime/10.;
    p.y +=  iTime/30.;


    p.z -= iTime/20.;
    */

    p = pmod(p,0.45);


    float d;

    d = length(p-0.1)-0.1;
    col = mix(col,vec3(0.1,0.4,0.6)/5.,smoothstep(0.002,0.,abs(d)-0.03));


    // --
    d = sdBox(p-0.01,vec3(0.14,0.3,0.1)/1.);
    col = mix(col,vec3(0.4,0.9,0.7)/0.6,smoothstep(0.001,0.,d-0.01));


    d = sdBox(p,vec3(0.14,0.3,0.1)/1.);
    col = mix(col,vec3(1,0.9,0.4),smoothstep(0.001,0.,d));

    col = mix(col,vec3(0.44,0.71,0.91),smoothstep(0.002,0.,abs(d)-0.01));



    // --

    d = sdBox(p-0.01,vec3(0.1,0.1,0.1)/1.);
    col = mix(col,vec3(0.1,0.4,0.6)/20.,smoothstep(0.002,0.,d));

    d = sdBox(p,vec3(0.1,0.1,0.1)/1.);
    col = mix(col,vec3(0.1,0.4,0.6),smoothstep(0.002,0.,d));




    p.yx *= rot(0.5*pi);
    d = sdBox(p,vec3(0.02,0.02,0.4)/1.);
    col = mix(col,vec3(0.9,0.4,0.6),smoothstep(0.002,0.,abs(d)-0.01));

    p.yx *= rot(-0.5*pi);
    col /= 1. + col;
    col = smoothstep(0.,1.,col*1.6);

    col = pow(col,vec3(0.45454));
    fragColor = vec4(col,1.0);
}
