const float extent = 30.0;

vec2 uv(vec2 p) 
{
    vec2 f = vec2(p) + vec2(100.0*snoise(p.xy*0.001 + 0.02*iTime),10.0*snoise(p.xy*0.001 + 0.15*iTime));

    //vec2 ctr = vec2(extent/2.0*iResolution.x/iResolution.y + extent/2.0*sin(0.25*iTime), extent/2.0 + cos(iTime*0.15));
    vec2 ctr = vec2(extent/2.0*iResolution.x/iResolution.y, extent/2.0);
    vec2 dt  = vec2(ctr-p);
    float l  = length(dt) / (1.0*extent);    
    float i  = max(0.1,1.0 - l);
    float s  = (1.0-2.0*abs(i-0.5));
    s = sin(s*3.14159*0.5);
    return f.xy + 5.5 * normalize(dt) * (s);
}

vec2 d(vec2 p) 
{   
    //return vec2(log_step); // uncomment to reveal distortion    

    float delta = 1.0;
    vec2  dudx  = ( uv(p - vec2(delta,0.0)) - uv(p + vec2(delta,0.0)) ) * 0.5 / delta;
    vec2  dudy  = ( uv(p - vec2(0.0,delta)) - uv(p + vec2(0.0,delta)) ) * 0.5 / delta;
    float du    = log_step / (0.000001 + length(vec2(dudx.x,dudy.x)));
    float dv    = log_step / (0.000001 + length(vec2(dudx.y,dudy.y)));
    du = max(log_step,du);
    dv = max(log_step,dv);
    return vec2(du,dv) * 2.0;
}


