float quantize(float d) {
    return pow(log_step,floor(log(d)/log(log_step)));
}

int d2level(float d) {
    return int(floor(log(d)/log(log_step)));
}

float level2d(int level) {
  return pow(log_step,round(float(level)));
}

float interpolate(float d) {
    int   level   = d2level(d);
    float df      = level2d(level);
    float dc      = level2d(level-1);    
    float linear  = (d - df) / (df - dc) / log_step;
    return smoothstep(0.0,1.0,linear);
}

float spacing(float d) {
    return 1.0/d;   
}

float shift(float d) {
    return d ;
}

float id2Lborder(int id, float d) {
    return (float(id) +       (shift(d) / spacing(d)) ) * spacing(d);
}

float id2center(int id, float d) {
    return (float(id) + 0.5 + (shift(d) / spacing(d)) ) * spacing(d);
}

int u2id(float u, float d) {
    return int(floor( u / spacing(d) - (shift(d) / spacing(d)) ));
}

int u2closestLBorderId(float u, float d) {
    int lid = u2id(u, d);
    if (u < id2center(lid, d)) {
        return lid;
    } else {
        return lid+1;
    }
}

float u2closestBorder (float u, float d) {
    return id2Lborder(u2closestLBorderId(u,d), d);
}

