int getLocalID(float u, float d) {
  // fine and coarse densities
  int   level = d2level(d);
  float df    = level2d(level  );
  float dc    = level2d(level-1);
  // enclosing band id
  int   lid     = u2id(u, df); 
  // get the interpolated borders
  float left,right;
  interpolatedBorders(lid,d,left,right);
  // adjust enclosing band after interpolation
  if      (u < left ) { 
      lid--;
  } else if (u > right) {
      lid++;
  } 
  return lid;
}

