uvec4 setId(uvec4 id,uint bit) // 128 bits IDs
{
    if (bit > 127u) return id;
    if (bit < 64u) {
        if (bit < 32u) { // 0 - 31 (w) LSBs
            return uvec4(id.x,id.y,id.z,id.w | (1u << bit));
        } else {         // 32 - 63 (x)
            return uvec4(id.x,id.y,id.z | (1u << (bit-32u)),id.w);            
        }
    } else {
        if (bit < 96u) { // 64 - 95 (x)
            return uvec4(id.x,id.y | (1u << (bit-64u)),id.z,id.w);            
        } else {         // 96 - 127 (x) MSBs
            return uvec4(id.x | (1u << (bit-96u)),id.y,id.z,id.w);            
        }
    }
}

