uvec4 getGlobalID(int lid, float d) {
  uvec4 gid = uvec4(0u);
  int start_level = d2level(d);
  for (int level = start_level; level > 0; level--) {
    float df      = level2d(level);
    float dc      = level2d(level-1);
    float lf_brdr = id2Lborder(lid  , df);
    float rf_brdr = id2Lborder(lid+1, df);
    // get the closest borders on the coarse bands
    int lid_brdr = u2closestLBorderId(lf_brdr, dc);
    int rid_brdr = u2closestLBorderId(rf_brdr, dc);
    if (lid_brdr == rid_brdr) {
      // this band is closing between parents, tag in ID (lower 96 bits)
      gid = setId(gid,uint(96 - level));
      lid  = u2id(lf_brdr, dc);
      if (level == start_level) {
        // the start band is appearing
        //gid.x = uint(lid_brdr);
        //return uvec4(0);
      }
    } else {
      // move up to parent
      lid  = lid_brdr;

    }      
  }
  // parent ids go into the most significant 32 bits
  gid.x = uint(lid);
  return gid;
}

