vec4 getRandomColor(uvec4 id) {
    vec3 tmp = vec3(fract(float(id.x^id.w)/213789.0), fract(float(id.y^1489u)/348789.0), fract(float(id.z^2789u)/5689.0));
    for (int i = 0; i < 16; i++) {
      tmp = fract(vec3(13.14159265359,1.41421356237,6.62607004) * tmp.zyx + tmp.yxz / vec3(13.14159265359,1.41421356237,6.62607004));
    }
    return vec4(tmp, 1.0);
}

