vec3 brdf(vec3 N, vec3 L, vec3 V, vec3 diff, vec3 spec, float exponent) 
{
    vec3 R = reflect(L,N);
    vec3 H = normalize(L+V);
    return diff * clamp(dot(N, L), 0., 1.)
         + spec * pow(max(0., dot(R,V)), exponent);
}



vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro) 
{
    vec3 l = lp - p;
    float dist = max(length(l), 0.01);
    float atten = min(1./(1. + dist*0.5), 0.2);
    l /= dist;

    vec3 n = normal;
   	vec3 r = reflect(-l, n);

    float dif = clamp(dot(l, n), 0.0, 1.0);
    float spe = pow(clamp(dot(r, -rd), 0.0, 1.0), 8.0);
    float fre = pow(clamp(1.0 + dot(n, rd), 0.0, 1.0), 2.0);
    float dom = smoothstep(-1.0, 1.0, r.y);

    vec3 lin = vec3(0.2);
    lin += 1.0*dif*vec3(1, .97, .85);
    lin += 2.5*spe*vec3(1, .97, .85)*dif;
    lin += 2.5*fre*vec3(1);
    lin += 0.5*dom*vec3(1);



    vec3 V = normalize(p-ro);
    vec3 tmp= brdf(n, l, V, vec3(dif) , vec3(spe), 32.0) ;
    lin+=tmp;


    return lin*atten;
}


