vec2 simplesmile(vec2 p)
{
    vec2 res;


    float sCuadro= sdBox( p,vec2(2.0));


    float scabeza= sdCircle( p, 0.8 );
    p.x=abs(p.x);
    float ojo= sdCircle( p-vec2(0.3,0.2), 0.2 );
    float sonrisaA= sdCircle( p-vec2(0.0,0.1), 0.4 );
    float sonrisaB= sdCircle( p-vec2(0.0,-0.1), 0.35 );

    float sonrisa=opS( sonrisaA, sonrisaB );


    sCuadro=opS(scabeza, sCuadro);


    scabeza=opS( ojo, scabeza );
    scabeza=opS( sonrisa,scabeza);

    res=opU2(res, vec2(sCuadro/fwidth(sCuadro) ,4.0) );

    res=opU2(res, vec2(scabeza/fwidth(scabeza) ,1.0) );
    res=opU2(res, vec2(ojo/fwidth(ojo) ,0.0) );
    res=opU2(res, vec2(sonrisa/fwidth(sonrisa) ,2.0) );

    return res;
}

