// Interpretation of idea by Zen00#3444 in Shadertoy discord.

// The sRGB stuff is actually useless in this shader since there is no blending.
// It's just standard practice.

vec3 sprite (vec2 uv ) {
  if (any(greaterThan(abs(uv-vec2(.5)),vec2(.5)))) return vec3(1.);
  return sRGBdecode(texelFetch(iChannel0, ivec2(uv*32.)+ivec2(3,0), 0).xyz);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord-R/2.)/R.yy;

    float t = pow(max(0.,-.2+mod(iTime/2.,1.2)),3.);

    float r = length(uv);
    float r2 = r+t;
    float a = t/r;
    vec2 m = cos(vec2(a,a-1.57));

    uv*= r2/r * mat2(m.x,m.y,-m.y,m.x);

    vec3 col = sprite(uv+.5);

    fragColor = vec4(sRGBencode(col),1.0);
}

// https://www.khronos.org/registry/OpenGL/extensions/EXT/EXT_sRGB.txt
vec3 sRGBdecode (vec3 e) {
  return mix(e/12.92,pow((e+.055)/1.055,vec3(2.4)),lessThan(vec3(.04045),e));
}
vec3 sRGBencode (vec3 v) {
  return mix(12.92*v,1.055*pow(v,vec3(.41666))-.055,lessThan(vec3(.0031308),v));
}
