#define PI 3.141592
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}
vec3 p(float t){
return palette( t, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
}
mat2 r(float a){
    float c=cos(a),s=sin(a);
    return mat2(c,s,-s,c);
}
vec3 fig(vec2 uv){
vec3 d  =vec3(0.);
    float n = 10.;+cos(iTime*2.)*10.;
    for(float i=0.;i<=n;i++){
        uv*=r(iTime*.2+PI/n);
     float l = length(uv)-.07*i;

    float q = step(cos(iTime+i)*.25+.75,(atan(uv.y,uv.x)+PI)/(2.*PI));
    l = abs(l)-.001;
    d+= vec3(p(i/n+fract(iTime*.75))*smoothstep(.02,.015,l)*q);
    }
    return d ;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy-.5* iResolution.xy)/iResolution.y;
    uv*=2.;
    if(mod(iTime,15.)<10.){
    uv = abs(uv)-.75;
    uv*=r(PI/4.);
    if(mod(iTime,15.)<5.){

    uv = abs(uv)-.125;
   }
   }
    vec3 d = fig(uv);

    vec3 col = vec3(d);


    fragColor = vec4(col,1.0);
}
