#define distrib(x) x * x * sign(x)
#define distribInv(x) sqrt(abs(x)) * sign(x)

vec2 nonlinearRepeat(in float x, in float interv) {
    float cell = distribInv(round(distrib(x) / interv) * interv);
    return vec2(x - cell, cell);
}

float sdBox(in vec2 p, in vec2 s) {
    return length(p - clamp(p, -s, s)) + min(0.0, max(abs(p.x) - s.x, abs(p.y) - s.y));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float unit = 2.0 / iResolution.y;
    vec3 color = vec3(0.0);

    float a = sin(iTime) * 1.57;
    float c = cos(a), s = sin(a);
    uv *= mat2(c, -s, s, c);

    vec2 rep = nonlinearRepeat(atan(uv.y, uv.x), 1.0);
    uv = sin(rep.x + vec2(1.57, 0.0)) * length(uv);
    uv.x -= 0.25;

    float d = sdBox(uv, vec2(0.05 / (abs(rep.y) + 1.0)));
    color += 0.125 + 0.125 * sin(d * 200.0);
    color = mix(color, mix(vec3(0.0, 0.0, 1.0), vec3(1.0, 0.0, 0.0), abs(rep.y / 3.14)), smoothstep(unit, 0.0, d));

    fragColor = vec4(color, 1.0);
}
