vec3 waves(vec2 uv, vec3 color)
{
    // To create the waves
	float wave_width;
	uv  = -.2 + 5.0 * uv;
	uv.y += 0.0;
	for(float i = .0; i < 15.; i++) {

		uv.y += (.07 * sin(uv.x + i/2.0 + iTime ));
		wave_width = abs(1. / (150.0 * uv.y));
		color += vec3(wave_width * 1.9, wave_width, wave_width * 1.5);
	}

    return color;
}

float circle(vec2 uv,float radius, float blur,  vec2 pos)
{

    float d = distance(pos,uv);
    float value = smoothstep(radius, radius + blur, d);

    return value; 
}

float rect (vec2 uv, vec2 pos, vec2 size)
{
	float value = step(pos.x - size.x/2.,uv.x) - step(pos.x + size.x/2.,uv.x);
    return value *= step(pos.y - size.y/2.,uv.y) - step(pos.y + size.y/2.,uv.y);
}

mat2 rotate2d(float angle)
{
    return mat2(cos(angle),-sin(angle), sin(angle), cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;

    uv.x *= ratio;

    vec2 moonPos = vec2(.2*ratio, .7);
    vec2 landPos = vec2(.5*ratio, .1);
    vec2 wheelPos = vec2(.7*ratio, .35);
    vec2 supportPos = vec2(.7*ratio, .25);

    float moon = circle(uv, .15, .05, moonPos);
   	float land = rect(uv, landPos, vec2(2., .1));
    float support = rect(uv, supportPos, vec2(.1, .3));

    vec3 water = waves(uv, vec3(-.05));

    //wheel
    uv-=wheelPos;
    uv*= rotate2d((iTime)*2.);
    uv+=wheelPos;

    float wheel = circle(uv,.3, 0., wheelPos);
    wheel -= circle(uv, .35, 0., wheelPos);

    //woodenbars
    float bar1 = rect(uv, wheelPos, vec2(.05, .8));
    float bar2 = rect(uv, wheelPos, vec2(.8, .05));
    float bolt = rect(uv, wheelPos, vec2(.05, .05));

	vec3 col = mix(vec3(.0627, .0823, .2), vec3(.4), support);
    col = mix(col, vec3(.4196, .1903, 0.), land);
    col = mix(col, vec3(.2784, .1882, .1176), wheel);
    col = mix(col, vec3(.2784, .1882, .1176), bar1);
    col = mix(col, vec3(.2784, .1882, .1176), bar2);
    col = mix(col, vec3(.1), bolt);
    col = mix(col, vec3(0., 0., 1.), water);
    col = mix(vec3(.9764, 1., .7607), col, moon);

	fragColor = vec4(col, 1.0);
}
