#define BIASED_NORMAL 	1
#define MAX_BOUNCES     3
#define MAX_DIST		200.
#define ABSORB			vec3(1., 0.1, .3)

const int maxIterations = 4;
const float majorRadius = 4.;

#define AieKick

// http://www.imajeenyus.com/mathematics/20121112_distance_estimates/distance_estimation_method_for_fractals.pdf

vec2 cmul(vec2 a, vec2 b)
{
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

vec2 csqr(vec2 v)
{
    return vec2(v.x * v.x - v.y * v.y, 2. * v.x * v.y );
}

#ifndef AieKick 

float julia2d(vec2 z, vec2 c)
{
    vec2 dz = vec2(1, 0);
    for (int i = 0; i < maxIterations; i++)
    {
        dz = 2. * cmul(z, dz);
        z = csqr(z) + c;
        if(dot(z,z) > 4.)
            break;
    }
    return length(z) * log(length(z)) / length(dz);
}

#else

// AieKick distance fonction is faster

float julia2d(vec2 z, vec2 c)
{
	float k = 1., h = 1.;    
    for (int i = 0;i < 5; i++)
    {
        h *= 4.*k;
		k = dot(z,z);
        if(k > 4.)
            break;
		z = csqr(z) + c;
    }
	return sqrt(k / h) * log(k);
}

#endif

float map(vec3 p)
{
	float	a = atan(p.x, p.z);
    vec2	c = 0.7 * vec2(mix(sin(iTime*0.592), sin(iTime*0.352), sin(a * 4.)), mix(sin(iTime*0.485), sin(iTime*0.625), sin(a * 3.)));     
    float	r = length(p.xz) - majorRadius;
	return  julia2d(vec2(r, p.y), c);
}

vec3 getSkyColor(vec3 rd)
{
    vec3 col = texture(iChannel0, rd).rgb;
    return col*col;
}


float rayMarch(in float sgn, in vec3 ro, in vec3 rd, in float offT)
{
  float t = offT;
  for (int i = 0; i < 300; i++)
  {
    float h = sgn * map(ro + rd * t);
    t += h * 0.2;						// Perf killer :(
    if (h < 0.001 || t > MAX_DIST)
        break;
  }
  return t;
}

#define EPS  0.001

#if BIASED_NORMAL
vec3 calcNormal(vec3 pos)
{
    float	ref;
    float	trans;
    vec3	absorb;
    vec3	col;
    vec2	eps = vec2(EPS, 0);
	float d = map(pos);
	return normalize(vec3(map(pos + eps.xyy) - d, map(pos + eps.yxy) - d, map(pos + eps.yyx) - d));
}
#else
vec3 calcNormal( in vec3 pos )
{
    const float ep = EPS;
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep ) + 
					  e.yyx*map( pos + e.yyx*ep ) + 
					  e.yxy*map( pos + e.yxy*ep ) + 
					  e.xxx*map( pos + e.xxx*ep ) );
}
#endif

vec3 Render(in vec3 ro, in vec3 rd)
{
    float sgn = 1.;
    float cref = 0.7;
    vec3  col = vec3(0);
    vec3  rel = vec3(1);
    float transp = 0.9;
    vec3  absorb = ABSORB;
    for(int i = 0; i < MAX_BOUNCES; i++)
    {
        float t = rayMarch(sgn, ro, rd, 0.02);
        if( t> MAX_DIST)
        {
            col += rel * getSkyColor(rd);
            break;
        }
        vec3 rabs = mix(absorb, vec3(0), (sgn + 1.) / 2.);
        vec3 beerlamb = exp(-rabs * t);
        vec3 p = ro + rd * t;
        vec3 n = sgn * calcNormal(p);
        vec3 refl = reflect(rd, n);
        vec3 refr = refract(rd, n, cref);
        //float fresnel = 1.0 - pow(dot(n, -rd), 2.);
        float fresnel = pow(1.0 - abs(dot(n, rd)), 2.0);
        float reflectorFactor = mix (0.2, 1.0, fresnel);
        float refractionFactor = mix (transp, 0., fresnel);

    	col += (1.0 - refractionFactor) * rel * beerlamb * getSkyColor(refl) * reflectorFactor;
    	rel *= refractionFactor * beerlamb;     

       	ro = p;     
        if (refr == vec3(0.0))
        {
            rd = refl;
        }
        else
        {
            rd = refr; 
            sgn *= -1.;
            cref = 1. / cref;
        }        
    }
    col += rel * getSkyColor(rd);
    return col;
}

vec3 contrast( in vec3 color, in float c)
{
    float t = 0.5 - c * 0.5; 
    return color * c + t;
}

vec3 vignette(vec3 color, vec2 q, float v)
{
    color *= 0.3 + 0.8 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), v);
    return color;
}

vec3 postProcess(in vec3 col, in vec2 q)
{
	col = pow(col, vec3(0.8));
	col = contrast(col, 1.2);
	col = vignette(col, q, 0.8);
	return col;
}


mat3 setCamera( in vec3 ro, in vec3 ta )
{
	vec3 cw = normalize(ta-ro);
	vec3 up = vec3(0, 1, 0);
	vec3 cu = normalize( cross(cw,up) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

mat3 genMat(in vec3 dir,in vec3 up)
{
	vec3 cw = normalize(dir);
	vec3 cu = normalize( cross(cw,up) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec3 tot = vec3(0.0);
#ifdef AA
	vec2 rook[4];
    rook[0] = vec2( 1./8., 3./8.);
    rook[1] = vec2( 3./8.,-1./8.);
    rook[2] = vec2(-1./8.,-3./8.);
    rook[3] = vec2(-3./8., 1./8.);
    for( int n=0; n<4; ++n )
    {
        // pixel coordinates
        vec2 o = rook[n];
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else //AA
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif //AA

        // camera
        float theta	= radians(360.)*(iMouse.x/iResolution.x-0.5); // + iTime*.2;
        float phi	= radians(90.)*(iMouse.y/iResolution.y-0.5)-1.;
        vec3 ro = 10. * vec3( sin(phi)*cos(theta),cos(phi),sin(phi)*sin(theta));
        vec3 ta = vec3( 0 );
        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta );
        //vec3 cd = ca[2];    

        vec3 rd =  ca*normalize(vec3(p,1.5));        

        vec3 col = Render(ro ,rd);


        tot += col;

#ifdef AA
    }
    tot /= 4.;
#endif

    tot = postProcess(tot,fragCoord / iResolution.xy);

	fragColor = vec4( sqrt(tot), 1.0 );
}
