/*
Using the inverse of smoothstep to analytically find a root bounded by two unique extrema of a cubic polynomial.

red    : cubic polynomial
orange : smoothstep rescaled to match the polynomial (only works with two unique extrema)
magenta: root found using inverse smoothstep, see RootBetweenCubicExtrema(..)
blue   : derivative of polynomial
*/


float cubic(float x) { return x*x*(3.0-2.0*x); }
float ilerp(float a, float b, float y) { return (y - a) / (b - a); }

vec4 Poly3Coeffs(float v0, float d0, float v1, float d1)
{
    vec4 C;

    C.x = v0;
    C.y = d0;
    C.z = -2.0 * d0 - d1 - 3.0 * (v0 - v1);
    C.w = d0 + d1 + 2.0 * (v0 - v1);

    return C;
}

vec3 Poly3D1Coeffs(float v0, float d0, float v1, float d1)
{
    vec3 C;

    C.x = d0;
    C.y = -4.0 * d0 - 2.0 * d1 - 6.0 * (v0 - v1);
    C.z = 3.0 * (d0 + d1) + 6.0 * (v0 - v1);

    return C;
}


float EvalPoly(float x, vec3 C)
{
    return C.x + x * (C.y + x * C.z);
}

float EvalPoly(float x, vec4 C)
{
    return C.x + x * (C.y + x * (C.z + x * C.w));
}

// https://math.stackexchange.com/questions/866331/numerically-stable-algorithm-for-solving-the-quadratic-equation-when-a-is-very
bool QuadraticRoots(inout vec2 roots, float c, float b, float a)
{
    roots = vec2(0.0);

    if (a == 0.0)
    {
        if (b == 0.0)
        {
            return c == 0.0;
        }

        roots.x = roots.y = -c / b;

        return true;
    }

    float D = b * b - 4.0 * a * c;

    if (D < 0.0) return false;

    //roots = (-b + vec2(1.0,-1.0)*sqrt(D)) / (2.0*a);
    roots.x = (-b - sign(b) * sqrt(D)) / (2.0 * a);
    roots.y = c / (a * roots.x);

    return true;
}

bool QuadraticRoots(inout vec2 roots, vec3 C)
{
    return QuadraticRoots(roots, C.x, C.y, C.z);
}

bool CubicExtrema(inout vec4 extrema, float v0, float d0, float v1, float d1)
{
    extrema = vec4(0.0);

    vec4 C = Poly3Coeffs(v0, d0, v1, d1);

    if (C.w == 0.0) return false;

    vec2 roots;
    bool DoRootsExist = QuadraticRoots(roots, C.yzw * vec3(1.0, 2.0, 3.0));

    if (DoRootsExist == false) return false;

    extrema.xy = vec2(roots.x, EvalPoly(roots.x, C));
    extrema.zw = vec2(roots.y, EvalPoly(roots.y, C));

    return true;
}

bool IsCubicSegSomeNeg(float v0, float d0, float v1, float d1)
{
    if (v0 < 0.0 || v1 < 0.0) return true;

    vec4 extrema;
    bool DoExtremaExist = CubicExtrema(extrema, v0, d0, v1, d1);

    if (DoExtremaExist == false) return false;

    if ((extrema.x >= 0.0 && extrema.x <= 1.0 && extrema.y < 0.0) ||
        (extrema.z >= 0.0 && extrema.z <= 1.0 && extrema.w < 0.0)) return true;

    return false;
}

bool IsCubicSegSomeNeg(vec2 vd0, vec2 vd1)
{
    return IsCubicSegSomeNeg(vd0.x, vd0.y, vd1.x, vd1.y);
}

// https://iquilezles.org/articles/ismoothstep
float inverse_smoothstep(float y)
{
    return 0.5 - sin(asin(1.0-2.0*y)/3.0);
}

float RootBetweenCubicExtrema(vec4 extrema)
{
    // solve: mix(extrema.y, extrema.w, cubic(ilerp(extrema.x, extrema.z, x))) == 0

    return mix(extrema.x, extrema.z, inverse_smoothstep(ilerp(extrema.y, extrema.w, 0.0)));
}

void mainImage( out vec4 outCol, in vec2 uv0 )
{
	vec2 uv = uv0 - 0.5;
    uv0 += 0.0;

    vec2 tex = uv0;
    tex -= iResolution.xy * vec2(0.1, 0.4);
    tex /= iResolution.xx * 0.125;

    tex *= 0.25+0.0375;


    vec3 col = vec3(0.0);

	col = mix(vec3(1.0), vec3(0.9), Grid(tex.xy * 4.0, 0.0));        
	col = mix(col, vec3(0.5), Grid(tex.xy * 1.0, 0.0));        
	col = mix(col, vec3(0.125), Cross(tex.xy * 1.0, 0.)); 	

    #if 0
    float v0 = 0.5;
    float d0 = 2.0;
    float v1 = -0.125;
    float d1 = 1.0;
    #elif 0
    float v0 = 0.5;
    float d0 = -3.0;
    float v1 = 0.125;
    float d1 = 0.4;
    #else
    float v0 = 0.5;
    float v1 = 0.;
    float d0 = v1-v0;
    float d1 = d0;
    #endif   

    v1 = sin(float(iTime))*0.5;

    #if 0
    if(IsCubicSegSomeNeg(v0, d0, v1, d1))
    {
        col = mix(col, vec3(0.5, 1.0, 0.0), 0.5*Dot(tex.xy, vec2(0.5, 0.5), 0.25));       
    }
    #endif

    vec4 extrema;
    bool DoExtremaExist = CubicExtrema(extrema, v0, d0, v1, d1);

    vec4 C  = Poly3Coeffs(v0, d0, v1, d1);
    vec3 Cd = Poly3D1Coeffs(v0, d0, v1, d1);

    vec2 roots;
    bool DoRootsExist = QuadraticRoots(roots, Cd);

    col = mix(col, vec3(0.0, 0.5, 1.0), 0.5*Graph(EvalPoly(tex.x, Cd) - tex.y, 0.5));       

    if(DoRootsExist)
    {
        col = mix(col, vec3(0.0, 0.5, 1.0), 0.5*Dot(tex.xy, vec2(roots.x, 0.0), 0.02));       
    	col = mix(col, vec3(0.0, 0.5, 1.0), 0.5*Dot(tex.xy, vec2(roots.y, 0.0), 0.02));  

        #if 1
        if(DoExtremaExist)
        {
    		col = mix(col, vec3(1.0, 0.5, 0.0), 0.5 * Graph(mix(extrema.y, extrema.w, cubic(ilerp(extrema.x, extrema.z, tex.x))) - tex.y, 7.0));       

            float x0 = RootBetweenCubicExtrema(extrema);
    		col = mix(col, vec3(1.0, 0.5, 1.0), 0.5* Dot(tex.xy, vec2(x0, 0.0), 0.05));       
    		col = mix(col, vec3(1.0, 0.0, 1.0), Dot(tex.xy, vec2(x0, 0.0), 0.02));       

    		col = mix(col, vec3(1.0, 0.0, 0.0), Dot(tex.xy, extrema.xy, 0.02));       
    		col = mix(col, vec3(1.0, 0.0, 0.0), Dot(tex.xy, extrema.zw, 0.02));   
        }
        #else
    	col = mix(col, vec3(1.0, 0.0, 0.0), Dot(tex.xy, vec2(roots.x, EvalPoly(roots.x, C)), 0.02));       
    	col = mix(col, vec3(1.0, 0.0, 0.0), Dot(tex.xy, vec2(roots.y, EvalPoly(roots.y, C)), 0.02));       
        #endif
    }

    col = mix(col, vec3(1.0, 0.0, 0.0), Graph(EvalPoly(tex.x, C) - tex.y, 0.5));       

    #if 1
    vec2 s = (uv0/iResolution.xy*2.0-1.0);
    s.x = 1.0-Pow2(s.x);    s.y = 1.0-Pow2(s.y);
    col *= mix(1.0, 0.4, Pow2(1.0-sqrt(s.x*s.y)));
    #endif

    outCol = vec4(GammaEncode(clamp01(col)), 1.0);    

}

#define PixelCount iResolution.xy
#define clamp01(x) clamp(x, 0.0, 1.0)
#define rsqrt inversesqrt

const float Pi = 3.14159265359;
const float Pi05 = Pi * 0.5;
const float Pi2  = Pi * 2.0;
const float RcpPi= 1.0 / Pi;

float Pow2(float x) {return x*x;}
float Pow3(float x) {return x*x*x;}
float Pow4(float x) {return Pow2(Pow2(x));}

float GammaEncode(float x) {return pow(x, 1.0 / 2.2);}
vec2 GammaEncode(vec2 x) {return pow(x, vec2(1.0 / 2.2));}
vec3 GammaEncode(vec3 x) {return pow(x, vec3(1.0 / 2.2));}
vec4 GammaEncode(vec4 x) {return pow(x, vec4(1.0 / 2.2));}

float ddxyLen(float v) { return length(vec2(dFdx(v), dFdy(v))); }
float ddxyRcpLen(float v) { return rsqrt( Pow2(dFdx(v)) + Pow2(dFdy(v)) ); }


float rescale(float v) { return v * ddxyRcpLen(v); }

float Graph(float f, float b)
{
    return clamp01(1.0 - (abs(rescale(f))-0.5-b)); 
}


float Line(float u, float b) { return Graph(u, b); }
float Lines(float u, float b) { return Graph(sin(u * Pi), b); }

float Grid(vec2 uv, float b)
{
    float xl = Lines(uv.x, b);    
    float yl = Lines(uv.y, b);

    return max(xl, yl);
}

float Cross(vec2 uv, float b)
{
    float xl = Line(uv.x, b);    
    float yl = Line(uv.y, b);

    return max(xl, yl);
}

float Dot(vec2 sp, vec2 dp, float dr)
{
    float v = length(sp - dp) - dr;

    if(v > dr) return 0.0;

    v *= ddxyRcpLen(v);
    v = 1.0 - clamp(v * 1.0, 0.0, 1.0);

    return v;
}
