#define tau 6.283

float u2f(uint x) {
    return uintBitsToFloat(0x3F800000u | (x >> 9)) - 1.;
}

uint hash(uint x, uint s) {
    x ^= s            ; x ^= x >> 16; x *= 0x7FEB352Du;
    x += s^0x7FEB352Du; x ^= x >> 15; x *= 0x846CA68Bu;
                        x ^= x >> 16;
    return x;
}

float hashf(uint x, uint s) {
    return u2f(hash(x,s));
}

float noise(float x, uint s) {
    float fx =          fract(x)  ;
    uint  ix = uint(int(floor(x)));

    float mx = (3.-2.*fx)*fx*fx;

    float l = hashf(ix+0u, s);
    float h = hashf(ix+1u, s);

    return mix(l,h,mx);
}

vec2 cossin(float a) {
    return vec2(cos(a), sin(a));
}

mat2 rot(float a) {
    vec2 cs = cossin(a);
    return mat2(cs, cs.yx * vec2(-1,1));
}

void mainImage(out vec4 col, in vec2 coord) {
    //coord *= rot(sin(iTime*.2)*.3);
    coord *= rot(.4);
    uint id = uint(int(floor(coord.x*.5)));

    float tmp = coord.y * .002;
    tmp += (iTime + noise(iTime * .2 + hashf(id, 0u), id) * 2.) * 2.;
    tmp += noise(tmp, id) * .6;
    tmp *= 10.;
    tmp += hashf(id, 1u);

    uint rand = hash(uint(int(floor(tmp))), id);

    float randf = u2f(rand);
    col.rgb = mix(vec3(.1,.2,.6),vec3(.1,.3,.7), randf*randf);

    //col.rgb = vec3(0);

    if((rand & 0xFFu) < 0x10u) {
        float a = fract(-tmp);
        a *= a;
        col.rgb *= 1.-a;
        col.rgb += vec3(a);
    }
}
