mat2x2 rotate(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2x2(c, -s, s, c);
}
vec3 yy(vec2 uv) {
    float angle = iTime*.5;
    vec3 color = vec3(.2,0,0);

    for(int i = 0 ; i < 4 ; i ++) {
        float l = length(uv);
        if( l > 1.) {
            //color = mix(color, vec3(1,1,1), smoothstep(1.,2.1,length(uv)));
            break;
        }


        uv *= rotate(sin(angle*float(i) + iTime)*float(i+1)*.11);
        uv.x -=sign(uv.x)/2.; 
        uv*=2.5;

        float nl = length(uv);
        color = vec3(smoothstep(uv.x*uv.x,uv.y,nl))*vec3(.2,0,0);
        color.rg+=vec2(.3,.2)*max(0.,smoothstep(0.1,0.0,abs(nl-1.1))*smoothstep(.9,1.,abs(uv.x))*(uv.y*5./float(i+1)+.5));
        //color = vec3(mix(vec3(sign(uv.y)*.5), color, smoothstep(.4, 0.1,abs(length(uv)-1.0))));

        //color = vec3(-(.2+uv.y)*.2*float(i+1),0,0);
    }

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
	fragColor = vec4(yy(uv*.55+vec2(0,.3)),1);
}
