#define EPS 1e-3
#define PI 3.14159265

mat2 r2(float a) {
	float s = sin(a), c = cos(a);
	return mat2(c,-s,s,c);
}

vec4 r4(float n) {
	#define R4P 1.1673039782614187
    return fract(.5+vec4(1./R4P,1./R4P/R4P,1./R4P/R4P/R4P,1./R4P/R4P/R4P/R4P)*n);
}
vec4 hash(vec4 a) {
    a = mod(abs(a),8273.97234);
    #define R4S(sw) floor(fract(.352347+dot(a,vec4(.001,.1,10.,100.).sw*2.23454))*20000.)
    return r4(R4S(xyzw)+R4S(yzwx)+R4S(wxyz)+R4S(zwxy));
}

float geo(vec3 p) {
	p.xy *= r2(p.z*.1);
	vec2 l = mod(abs(p.xz),2.)-1.;
	return min(p.y+2.,length(max(abs(l)-.2,0.)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

	vec3 rp = vec3(0,0,time),
		rd = normalize(vec3(uv.xy,.7)),
		c = vec3(0);

	for (int i = 0; i < 40; i++) {
		float dst = geo(rp);
		if (dst <= 0.) break;
		rp += rd*(dst+EPS);
	}

	fragColor = vec4(mix(vec3(.16,.25,.18),vec3(1,1,.6),pow(length(rp.xy)*.1,2.2)),1);
}
