void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0.0, 0.0, 3.0);
    vec3 rd = normalize(vec3(uv, -1.0));
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    float distTraveled = 0.0;
    for (int i=0; i < 100; i++) {
        vec3 pos = ro + rd * distTraveled;

        pos.z -= 4.0 * iTime;
        float c = cos(iTime), s = sin(iTime);
        pos.xy = vec2(pos.x * c - pos.y * s, pos.x * s + pos.y * c);

        vec3 q = abs(pos);
        float dist = 1.0 - max(q.x, q.y);
        if (dist < 0.001) {
            vec2 uv = pos.xy;
            if (q.x > q.y) {
                uv = pos.yz;
            }

            if (q.y > q.x) {
                uv = pos.xz;
            }

            fragColor += mod(floor(uv.x * 4.0) + floor(uv.y * 4.0), 2.0) * ((5.0 - distTraveled) / 5.0);
            break;
        }

        if (distTraveled > 1000.0) {
            break;
        }

        distTraveled += dist;
    }
}
