#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float add_Capsule(vec3 p,vec3 a,vec3 b, float r){
    vec3 ab = b-a;
    vec3 ap = p-a;
    float t = dot( ab, ap ) / dot( ab, ab);
    t = clamp (t,0.,1.);
    vec3 c = a +t*ab;
    float d = length(p -c) - r ;
    return d;

}

float GetDist(vec3 p){
	vec4 s = vec4(0, 1, 6, 1);
    float sphereDist = length (p - s.xyz)-s.w;
    float planeDist = p.y;
    float d = min (sphereDist, planeDist);
    return d;
}

float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.;
    for (int i=0; i< MAX_STEPS; i++){
        vec3 p = ro +rd*dO;
        float ds = GetDist(p);
        dO += ds;
        if (dO>MAX_DIST || ds<SURF_DIST) break;
        }
    return dO; 
}

vec3 GetNormal(vec3 p){
	float d = GetDist(p);
    vec2 e = vec2(.01,0);
    //
    vec3 n = d - vec3(
       //p -vec3(.01,0,0) GLSL is that the properties can be combined in any order you want,
       //which makes it easy to cast and mix values and is called swizzle.
        GetDist(p - e.xyy),
        GetDist(p - e.yxy),
        GetDist(p - e.yyx));
    return normalize(n);
}

float GetLight(vec3 p){
    vec3 lightPos = vec3 (0, 5, 6);
    lightPos.xz += vec2(sin(iTime),cos(iTime))*2.;
    vec3 l = normalize (lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n,l),0.,1.);
    float d = RayMarch(p+n*SURF_DIST*2.,l);
    if (d<length(lightPos-p)) dif *= .1;
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates to 0 with a resolution of 5 
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	// Set color to black
    vec3 col = vec3(0);
    //
    vec3 ro = vec3(0, 1, 0);
    //
    vec3 rd = normalize(vec3 (uv.x, uv.y, 1));

    float d = RayMarch(ro,rd);
    // set point light long d 
    vec3 p = ro + rd * d;
    // dif is difuse
    float dif = GetLight(p);

    //d/=6.;
    //col = vec3(d);
    col = vec3 (dif);

    // Send output to color named col to screen
    fragColor = vec4(col,1.0);
}
