vec2 r(inout vec2 x,float y)
{
	return x=vec2(cos(y)*x.x-sin(y)*x.y,sin(y)*x.x+cos(y)*x.y);
}

// julia function
float s(vec3 x,float y) 
{
	float R=y;//1.21;
	for(int i=0;i<50;i++)
    {
        r(x.xy,R);
        r(x.zx,R);
        r(x.yz,R);
        x=(abs(x-vec3(.7,.4,-2))+vec3(.7,.4,-2))*1.13+vec3(-1,.02,-.85);
    }
	return length(x)/455.0;
}

void mainImage(out vec4 O,vec2 c)
{
	float z = (iTime+16.) * 1000.;

	float d=0.;
    float o=0.;
    float p=0.;
    float u=z/2056.;
    float b=mod(u,4.)<3.?1.-mod(u,1.):0.;
    float v=u< 8.?1.9:
    		u<16.?1.9+.05*b:
    		u<24.?-2.-b:
    		u<32.?.25-(u-24.)/32.:
    		u<40.?1.3-.1*b:
    		1.9+.05*b;

    vec3 t=vec3(0.,0.,u<8.?16.-u*2.:5.+3.*sin(u));    
    vec3 y=normalize(vec3(c/iResolution.xy-.5,-.5));

    r(t.xz,-u/2.);
    r(t.yz,-u/2.);
    r(y.xz,-u/2.);
    r(y.yz,-u/2.);

	for(int i=0;i<150&&d<50.;i++)
    {
        b=p;
        d+=p=s(t+d*y,v);
        o+=exp(-abs(p-b));
    }
	O = vec4(.8,.9,1.,0.)*.014*o;

    return;
}
