vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

float particleDistance(int id, vec2 p)
{
    return distance(getParticle(id).xy, p);
}

float gauss(float x, float r)
{
    x/=r;
    return exp(-x*x);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 col, in vec2 pos )
{
    ivec2 p = ivec2(pos);
    get(p);
   	float y = 0., x = 0.;
    for(int i = 0; i < CN; i++)
    {
        float d =particleDistance(ids[i],pos); 
         d /= 2.*prad;
        y += -2.*sin(d)/(k*d);
        x += gauss(d,2.*prad);
    }
    col = .5+.5*sin(0.1*vec4(1,2,3,4)*(3.*y + x*40.)); 
}

//particle buffer
//particle buffer

float gauss(float x, float r)
{
    x/=r;
    return exp(-x*x);
}

//force scalar
float F(float d)
{
    d /= 2.*prad;
    return -ff*sin(d)/(k*d);
}

//force vector
vec2 Fv(vec2 p0, vec2 p1)
{
    if(!all(equal(p0,p1)))
   	 return normalize(p1-p0)*F(distance(p1,p0));
    else
     return vec2(0.);
}

//get particle from array
vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);
    if(p.x < N.x && p.y < N.y)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch1, pos);

        //neighbors
   		get(ivec2(U.xy));

        vec2 F = vec2(0.,0.);

        vec4 p0 = getParticle(ids[0]);
        for(int i = 1; i < CN; i++)
        {
            vec4 pi = getParticle(ids[i]);
        	F += Fv(U.xy, pi.xy);    
        }

        if(iMouse.z > 0.) 
        {
            float d = distance(iMouse.xy, U.xy);
            F += 3.*normalize(iMouse.xy - U.xy)/(sqrt(d)+2.);
        }

        U.zw += dt*F;
        U.zw *= 1. - 0.004*dt*length(U.zw); //cooling
        U.xy += U.zw*dt;

        //border conditions
        if(size.x - U.x < 2.) U.z = -abs(U.z);
        if(U.x < 2.) U.z = abs(U.z);
        if(size.y - U.y < 2.) U.w = -abs(U.w);
        if(U.y < 2.) U.w = abs(U.w);

        //this pixel value
        if(iFrame<3)
        {
            U.xy = size*hash22(pos)+1.;
			U.zw = 2.*(hash22(3.14159*pos) - 0.5);
        }
    }
    else discard;
}

//definitions
#define size iResolution.xy
#define pixel(a, p) texture(a, p/vec2(textureSize(a,0)))
#define texel(a, p) texelFetch(a, ivec2(p), 0)
#define ch0 iChannel0
#define ch1 iChannel1
#define ch2 iChannel2
#define ch3 iChannel3
#define PI 3.14159265

#define N ivec2(12,12)
#define dt 0.5
#define prad 1.
#define k 0.3
#define ff 4.

int tot_n = N.x*N.y;

//hash functions
//https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p) - 0.5;
}

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

//a rainbow colormap from Matlab
float interpolate(float val, float y0, float x0, float y1, float x1) 
{
    return (val-x0)*(y1-y0)/(x1-x0) + y0;
}

float base(float val) 
{
    if ( val <= -0.75 ) return 0.0;
    else if ( val <= -0.25 ) return interpolate( val, 0.0, -0.75, 1.0, -0.25 );
    else if ( val <= 0.25 ) return 1.0;
    else if ( val <= 0.75 ) return interpolate( val, 1.0, 0.25, 0.0, 0.75 );
    else return 0.0;
}

vec3 jet_colormap(float v)
{
    return vec3(base(v - 0.5),base(v),base(v + 0.5));
}

vec3 jet_range(float v, float a, float b)
{
    return jet_colormap(2.*clamp((v-a)/(b-a),0.,1.) - 1.);
}

uniform sampler2D fake;

//closest particle ids
#define CN 8
int ids[CN];
//particle distances
float ds[CN];

ivec2 one2two(int a)
{
    return ivec2(a%tot_n, a/tot_n);
}

int two2one(ivec2 a)
{
    return a.x + tot_n*a.y;
}

void toarr(inout int b[CN], ivec2 a, int i)
{
    b[i] = a.x;
    b[i+1] = a.y;
}

void init()
{
    for(int i = 0; i<CN; i++)
    {
        ids[i] = -1;
        ds[i] = 1e10;
    }
}

void getA(inout int b[CN], ivec2 p)
{ 
    ivec4 a = floatBitsToInt(texel(fake, p/2));
    toarr(b, one2two(a.x), 0);
    toarr(b, one2two(a.y), 2);
    toarr(b, one2two(a.z), 4);
    toarr(b, one2two(a.w), 6);
}

void get(ivec2 p)
{
    getA(ids, p);
}

vec4 save()
{
    ivec4 v = ivec4(two2one(ivec2(ids[0],ids[1])),
                    two2one(ivec2(ids[2],ids[3])),
                    two2one(ivec2(ids[4],ids[5])),
                    two2one(ivec2(ids[6],ids[7])));
    return intBitsToFloat(v);
}

ivec2 i2xy(int id)
{
    return ivec2(id%N.x, id/N.x);
}

int xy2i(ivec2 p)
{
    return p.x + p.y*N.x;
}

void swap(inout int x, inout int y)
{
    int b = x;
    x = y;
    y = b;
}

void swap(inout float x, inout float y)
{
    float b = x;
    x = y;
    y = b;
}

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

float particleDistance(int id, vec2 p)
{
    return distance(getParticle(id).xy, p);
}

void insert(int id, float d, int i)
{
    //shift all by 1
    for(int j = CN-1; j > i; j--)
    {
        ids[j] = ids[j-1];
        ds[j] = ds[j-1];
    }

    //insert
    ids[i] = id; 
    ds[i] = d;
}

void sort(int utemp, ivec2 p)
{
    vec2 pos = vec2(p)+1.;
    float dtemp = particleDistance(utemp, pos);  
    //sorting
    for(int i = 0; i < CN; i++)
    {
        if(ids[i] == utemp) return;

        if(ids[i] < 0 || ds[i] >= dtemp)
        {
            insert(utemp, dtemp, i);
            return;
        }
    }
}

void upd(ivec2 p, ivec2 dx)
{
    int t[CN];
    getA(t, max(p + dx, ivec2(0,0)));

	for(int i = 0; i < CN; i++)
    {
   		if(t[i]>=0)
        	sort(t[i], p);
    }
}

void randupd(ivec2 p )
{
    for(int i = 0; i < 6; i++)
    {
        //go through the entire array sudorandomly
        sort(((iFrame + p.x + p.y*int(iResolution.x))^(1<<i))%tot_n, p);
    }
}

void updrad(ivec2 p, int R)
{
    int r = 1<<R;
    upd(p, ivec2(r, 0));
    upd(p, ivec2(-r, 0));
    upd(p, ivec2(0, r));
    upd(p, ivec2(0, -r));
}

void update(ivec2 p)
{
    randupd(p);
    upd(p, ivec2(0));
    updrad(p, 0);
    updrad(p, 2);
}

void mainImage( out vec4 U, in vec2 pos )
{
    pos *= 2.;
    if(pos.x < iResolution.x && pos.y < iResolution.y) //8x voronoi particle tracking but in 2x lower res
    {
        ivec2 p = 2*ivec2(pos/2.);

        init();

        update(p);

        U = save();
    } else discard;
}
