void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;

    #ifdef BLUR
    vec3 o=vec3(0);
    vec3 c0_real,c1_real;
    vec3 c0_imag,c1_imag;

    vec2 c0_r,c1_r;
    vec2 c0_g,c1_g;
    vec2 c0_b,c1_b;

    vec2 c0_2pass,c1_2pass;

    for(int i=-CONV_SIDE;i<=CONV_SIDE;i++){
        c0_real=col(iChannel0,uv+vec2(0,i)/iResolution.y);c1_real=col(iChannel2,uv+vec2(0,i)/iResolution.y);
        c0_imag=col(iChannel1,uv+vec2(0,i)/iResolution.y);c1_imag=col(iChannel3,uv+vec2(0,i)/iResolution.y);

        c0_r=vec2(c0_real.r,c0_imag.r);c1_r=vec2(c1_real.r,c1_imag.r);
        c0_g=vec2(c0_real.g,c0_imag.g);c1_g=vec2(c1_real.g,c1_imag.g);
        c0_b=vec2(c0_real.b,c0_imag.b);c1_b=vec2(c1_real.b,c1_imag.b);

        c0_2pass=vec2(c0_conv_real(STEP*float(i)),c0_conv_imag(STEP*float(i)));c1_2pass=vec2(c1_conv_real(STEP*float(i)),c1_conv_imag(STEP*float(i)));

        c0_r=cMul(c0_r,c0_2pass);c1_r=cMul(c1_r,c1_2pass);
        c0_g=cMul(c0_g,c0_2pass);c1_g=cMul(c1_g,c1_2pass);
        c0_b=cMul(c0_b,c0_2pass);c1_b=cMul(c1_b,c1_2pass);

        o+=vec3(c0_r.x,c0_g.x,c0_b.x)*c0_weight_real+
           vec3(c0_r.y,c0_g.y,c0_b.y)*c0_weight_imag+
           vec3(c1_r.x,c1_g.x,c1_b.x)*c1_weight_real+
		   vec3(c1_r.y,c1_g.y,c1_b.y)*c1_weight_imag;
    }

    fragColor = vec4(o/AREA,1.0);

    #else
    fragColor=vec4(col(iChannel3,uv),1);

    #endif
}

#define BLUR
#define BRIGHT_SPOTS
const float pi=4.*atan(1.);

const int CONV_SIDE=10; //20 (bigger blur and performance comparation with convoluted one)
const float BLUR_RADIUS=10.; //20 (bigger blur and performance comparation with convoluted one)
const float BRIGHT_SPOT_TRESHOLD=0.5;

const float AREA=min(pi*BLUR_RADIUS*BLUR_RADIUS,float(4*CONV_SIDE*CONV_SIDE));
const float STEP=1.1/BLUR_RADIUS;

vec3 BriSp(vec3 p){
    #ifdef BRIGHT_SPOTS
    if(p.x+p.y+p.z>BRIGHT_SPOT_TRESHOLD*3.)p=(1./(1.-p)-1.)*(1.-BRIGHT_SPOT_TRESHOLD);
    p=clamp(p,0.,200.);
	#endif
    return p;
}

float c0_weight_real=0.411259;
float c0_weight_imag=-0.548794;
float c1_weight_real=0.513282;
float c1_weight_imag=4.561110;

float c0_conv_real(float x){
	return cos(5.268909*x*x)*exp(-0.886528*x*x);
}

float c0_conv_imag(float x){
	return sin(5.268909*x*x)*exp(-0.886528*x*x);
}

float c1_conv_real(float x){
	return cos(1.558213*x*x)*exp(-1.960518*x*x);
}

float c1_conv_imag(float x){
	return sin(1.558213*x*x)*exp(-1.960518*x*x);
}

vec2 cMul(vec2 a,vec2 b){
    return vec2(
        a.x*b.x-a.y*b.y,
        a.x*b.y+b.x*a.y
    );
}

vec3 col(sampler2D sampler,vec2 p){
	return texture(sampler,p).rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-vec2(iResolution.y/2.,0))/iResolution.y;

    vec3 o=vec3(0);
    for(int i=-CONV_SIDE;i<=CONV_SIDE;i++){
    	o+=BriSp(col(iChannel0,uv+vec2(i,0)/iResolution.y))*c0_conv_real(STEP*float(i));
    }
    fragColor = vec4(o,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-vec2(iResolution.y/2.,0))/iResolution.y;

    vec3 o=vec3(0);
    for(int i=-CONV_SIDE;i<=CONV_SIDE;i++){
    	o+=BriSp(col(iChannel0,uv+vec2(i,0)/iResolution.y))*c0_conv_imag(STEP*float(i));
    }
    fragColor = vec4(o,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-vec2(iResolution.y/2.,0))/iResolution.y;

    vec3 o=vec3(0);
    for(int i=-CONV_SIDE;i<=CONV_SIDE;i++){
    	o+=BriSp(col(iChannel0,uv+vec2(i,0)/iResolution.y))*c1_conv_real(STEP*float(i));
    }
    fragColor = vec4(o,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-vec2(iResolution.y/2.,0))/iResolution.y;

	#ifdef BLUR
    vec3 o=vec3(0);
    for(int i=-CONV_SIDE;i<=CONV_SIDE;i++){
    	o+=BriSp(col(iChannel0,uv+vec2(i,0)/iResolution.y))*c1_conv_imag(STEP*float(i));
    }
    fragColor = vec4(o,1.0);

    #else
    fragColor = vec4(BriSp(col(iChannel0,uv)),1);

    #endif
}
